function Parse-Records {
    param([string[]]$Lines)
    $records = @()
    $inRecord = $false
    $current = @{}

    foreach ($line in $Lines) {
        if ($line -match '^@record v1') {
            $inRecord = $true
            $current = @{}
        } elseif ($line -match '^@@$' -and $inRecord) {
            $records += [PSCustomObject]$current
            $inRecord = $false
        } elseif ($inRecord -and $line -match '^(\w+)\s*=\s*(.+)$') {
            $current[$Matches[1]] = $Matches[2]
        }
    }
    return $records
}

# Usage:
$lines = Get-Content 'io/chat.txt'
$records = Parse-Records -Lines $lines
foreach ($r in $records) {
    Write-Host "From: $($r.from) | CM1: $($r.cm1) | Payload: $($r.payload)"
}

