.fade-in{animation:fadeIn .3s ease-in}
.fade-out{animation:fadeOut .3s ease-out}
.slide-up{animation:slideUp .3s ease-out}
.slide-down{animation:slideDown .3s ease-in}
@keyframes fadeIn{from{opacity:0}to{opacity:1}}
@keyframes fadeOut{from{opacity:1}to{opacity:0}}
@keyframes slideUp{from{transform:translateY(20px);opacity:0}to{transform:translateY(0);opacity:1}}
@keyframes slideDown{from{transform:translateY(0);opacity:1}to{transform:translateY(20px);opacity:0}}
.transition{transition:all .2s ease}
.hover-accent:hover{color:#7cd3ff;border-color:#7cd3ff}
.hover-glow:hover{box-shadow:0 0 12px rgba(124,211,255,.3)}
