```javascript
class AdvancedSCXCompressor {
  constructor() {
    // Multi-layer dictionary
    this.dictionaries = {
      glyphs: new Map(),     // [Pop] ‚Üí ‚üÅP
      base64: new Map(),     // W1BvcA== ‚Üí ¬ßP
      patterns: new Map()    // Repeated sequences
    };

    this.stats = {
      originalSize: 0,
      compressedSize: 0,
      savings: 0
    };
  }

  // Main compression pipeline
  compress(glyphCode) {
    this.stats.originalSize = glyphCode.length;

    // Pipeline:
    // 1. Glyph ‚Üí SCX
    const step1 = this.glyphToSCX(glyphCode);

    // 2. SCX ‚Üí Base64
    const step2 = btoa(step1);

    // 3. Base64 ‚Üí Pattern Compression  
    const step3 = this.compressBase64Patterns(step2);

    // 4. Final traditional compression
    const final = this.lzCompress(step3);

    this.stats.compressedSize = final.length;
    this.stats.savings = 1 - (final.length / glyphCode.length);

    return final;
  }

  glyphToSCX(glyphCode) {
    // Replace common glyphs with symbols
    const replacements = {
      '\\[Pop\\s+': '‚üÅP‚üÅ',
      '\\[Wo\\s+': '‚üÅW‚üÅ',
      '\\[Sek\\s+': '‚üÅS‚üÅ',
      '\\[Ch\'en\\s+': '‚üÅC‚üÅ',
      '\\[Yax\\s+': '‚üÅY‚üÅ',
      '\\[Xul\\]': '‚üÅX',
      '‚Üí': '‚üÅ‚Üí'
    };

    let result = glyphCode;
    for (const [pattern, replacement] of Object.entries(replacements)) {
      result = result.replace(new RegExp(pattern, 'g'), replacement);
    }

    return result;
  }

  compressBase64Patterns(base64) {
    // Find repeated sequences in base64
    const sequences = this.findRepeatedSequences(base64);

    let compressed = base64;
    sequences.forEach((seq, index) => {
      if (seq.count > 2 && seq.length > 4) {
        const symbol = `¬ß${index}`;
        compressed = compressed.replaceAll(seq.value, symbol);
        this.dictionaries.base64.set(symbol, seq.value);
      }
    });

    return compressed;
  }

  findRepeatedSequences(str) {
    const sequences = [];
    const minLength = 4;
    const maxLength = 12;

    for (let len = minLength; len <= maxLength; len++) {
      for (let i = 0; i <= str.length - len; i++) {
        const seq = str.substr(i, len);
        const count = (str.match(new RegExp(seq, 'g')) || []).length;

        if (count > 1 && !sequences.some(s => s.value.includes(seq))) {
          sequences.push({ value: seq, count, length: len });
        }
      }
    }

    return sequences.sort((a, b) => b.count - a.count);
  }

  lzCompress(str) {
    // Simple LZ-like compression
    const dict = new Map();
    let result = '';
    let buffer = '';

    for (const char of str) {
      buffer += char;
      if (!dict.has(buffer)) {
        dict.set(buffer, dict.size);
        result += buffer;
        buffer = '';
      }
    }

    return result + buffer;
  }

  decompress(compressed) {
    // Reverse the pipeline
    const step4 = this.lzDecompress(compressed);
    const step3 = this.decompressBase64Patterns(step4);
    const step2 = atob(step3);
    const step1 = this.SCXToGlyph(step2);

    return step1;
  }
}

