                    '100%' => ['--opacity' => '0.5']
                ]
            ]
        ];

        // Store animation
        $this->engine['animations'][] = [
            'id' => 'anim_' . $activation . '_' . time(),
            'type' => $activation,
            'css' => $cssActivation,
            'active' => true
        ];

        return [
            'action' => 'yax',
            'activation' => $activation,
            'cssActivation' => $cssActivation,
            'status' => 'started'
        ];
    }

    private function handleSek($token, $params) {
        $action = $token['value'] ?? 'render';

        // Execute Three.js action via PHP
        $threeJSCommand = [
            'type' => 'execute',
            'action' => $action,
            'parameters' => $params,
            'cssFeedback' => [
                'selector' => '.kuhul-execution-' . $action,
                'properties' => [
                    '--execution-status' => 'complete',
                    '--render-time' => time(),
                    '--fps' => '60'
                ]
            ]
        ];

        // Store execution
        $this->engine['quantumStates']['executions'][] = $threeJSCommand;

        return [
            'action' => 'sek',
            'command' => $action,
            'threeJS' => $threeJSCommand,
            'result' => 'rendered'
        ];
    }

    private function handleKayab($token, $params) {
        // Start quantum animation sequence
        return [
            'action' => 'kayab',
            'status' => 'animation-started',
            'quantumLoop' => 'active',
            'cssOverride' => '.kuhul-quantum-loop { --loop-state: infinite; }'
        ];
    }

    private function handleKumku($token, $params) {
        // End animation loop
        return [
            'action' => 'kumku',
            'status' => 'animation-ended',
            'quantumLoop' => 'collapsed',
            'cssOverride' => '.kuhul-quantum-loop { --loop-state: stopped; }'
        ];
    }

    private function finalizeResponse($responses) {
        // Compile final response with CSS AST
        $compiledCSS = $this->compileCSSAST();

        return [
            'status' => 'success',
            'kuhul' => 'processed',
            'timestamp' => time(),
            'responses' => $responses,
            'cssAST' => $compiledCSS,
            'engineState' => $this->getEngineSnapshot(),
            'quantumSignature' => bin2hex(random_bytes(16))
        ];
    }

    private function compileCSSAST() {
        $css = "/* K'UHUL THREE.JS CSS AST COMPILATION */\n";
        $css .= "/* Generated: " . date('Y-m-d H:i:s') . " */\n\n";

        foreach ($this->engine['cssAST'] as $node) {
            $css .= $node['selector'] . " {\n";
            foreach ($node['properties'] as $key => $value) {
                $css .= "  " . $key . ": " . $value . ";\n";
            }
            $css .= "}\n\n";

            if (isset($node['animations'])) {
                foreach ($node['animations'] as $name => $keyframes) {
                    $css .= "@keyframes " . $name . " {\n";
                    foreach ($keyframes as $percent => $properties) {
                        $css .= "  " . $percent . " {\n";
                        foreach ($properties as $prop => $val) {
                            $css .= "    " . $prop . ": " . $val . ";\n";
                        }
                        $css .= "  }\n";
                    }
                    $css .= "}\n\n";
                }
            }
        }

        return $css;
    }

    private function getEngineSnapshot() {
        return [
            'scenes' => count($this->engine['scenes']),
            'meshes' => count($this->engine['meshes']),
            'animations' => count($this->engine['animations']),
            'cssNodes' => count($this->engine['cssAST']),
            'quantumStates' => count($this->engine['quantumStates'] ?? [])
        ];
    }
}

