function Invoke-CM1Verify {
    param([string]$Text)
    # CM-1: control character DFA — PASS if no illegal chars
    foreach ($ch in $Text.ToCharArray()) {
        $code = [int]$ch
        if ($code -lt 0x20 -and $code -ne 0x0A -and $code -ne 0x0D -and $code -ne 0x09) {
            if ($code -eq 0x00) { return 'ILLEGAL' }  # null byte
            return 'FAIL'  # other control chars
        }
    }
    return 'PASS'
}

# Usage:
$result = Invoke-CM1Verify -Text $record.payload
if ($result -ne 'PASS') {
    Add-Content $traceFile "CM1:$result:$($record.from):$(Get-Date -Format o)"
    return  # do not process
}

function Invoke-KUHUL-TSG {
    param([string]$Payload)
    $tokens = $Payload -split '\s+' | Where-Object { $_ -ne '' }
    $signal = @{
        tokens    = $tokens
        count     = $tokens.Count
        timestamp = (Get-Date -Format 'o')
        intent    = ($tokens | Select-Object -First 3) -join '_'
        payload   = $Payload
    }
    return $signal
}

# Usage in orchestrator main loop:
$signal = Invoke-KUHUL-TSG -Payload $record.payload
$mass   = $signal.count * 0.1
Add-Content $streamFile ">> $($signal.timestamp) | inference | intent=$($signal.intent) mass=$mass"

$state = 'INIT'
$chatFile   = Join-Path $PSScriptRoot 'io/chat.txt'
$streamFile = Join-Path $PSScriptRoot 'io/stream.txt'
$lastLine   = 0

Set-State 'READY'

while ($state -ne 'HALT') {
    $lines = Get-Content $chatFile -ErrorAction SilentlyContinue
    if ($lines.Count -gt $lastLine) {
        Set-State 'RUNNING'
        $newLines = $lines[$lastLine..($lines.Count - 1)]
        $lastLine = $lines.Count

        foreach ($block in (Parse-Records $newLines)) {
            $cm1 = Invoke-CM1Verify -Text $block.payload
            if ($cm1 -ne 'PASS') { continue }
            $signal = Invoke-KUHUL-TSG -Payload $block.payload
            $result = Invoke-SCXQ2-Infer -Signal $signal
            Add-Content $streamFile ">> $($signal.timestamp) | inference | $result"
        }
        Add-Content $streamFile '>> EOS'
    } else {
        Set-State 'IDLE'
    }
    Start-Sleep -Seconds 1
}

function Save-Snapshot {
    param([hashtable]$State)
    $snapDir = Join-Path $PSScriptRoot 'io/snapshot'
    if (-not (Test-Path $snapDir)) { New-Item -ItemType Directory -Path $snapDir | Out-Null }

    $timestamp = Get-Date -Format 'yyyyMMdd_HHmmss'
    $snapFile  = Join-Path $snapDir "snap_$timestamp.json"
    $State | ConvertTo-Json -Depth 10 | Set-Content $snapFile

    # Rotate: keep only last 10 snapshots
    $snaps = Get-ChildItem $snapDir -Filter 'snap_*.json' | Sort-Object Name
    if ($snaps.Count -gt 10) {
        $snaps[0..($snaps.Count - 11)] | Remove-Item
    }
}

# Called after each inference cycle:
