  constructor(ctx = {}) {
    this.ctx = ctx;
  }

  resolve(ref) {
    if (typeof ref !== 'string' || !ref.startsWith('$')) return ref;
    const path = ref.slice(1).split('.');
    let cur = this.ctx;
    for (const p of path) {
      cur = cur?.[p];
