        scores = {}
        for state in admissible_states:
            score = 0
            # Business plugin: prefers profitable states
            if self.type == "business_rules":
                score += state.calculate_profit() * self.weight
            # UI plugin: prefers efficient DOM states  
            elif self.type == "ui_optimization":
