      ⟁ system "gameplay.matching"
        ⟁ on_board_update
          # Check for π-shaped patterns (circular matches)
          ⟁π⟁ detect_π_patterns board → patterns

          ⟁@if⟁ patterns.length > 0
            ⟁then⟁
              ⟁Wo⟁ clear_patterns patterns
              ⟁π⟁ calculate_π_bonus patterns → bonus
              ⟁Wo⟁ add_score bonus * constants.φ

          # Check linear matches
          ⟁Sek⟁ find_linear_matches 4 → matches
          ⟁Wo⟁ process_matches matches
