      font-family: 'SF Mono', monospace;
      position: relative;
      overflow: hidden;
    }

    .kuhul-button::before {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, 
        transparent, 
        rgba(255, 255, 255, 0.2), 
        transparent);
      transition: left 0.5s;
    }

    .kuhul-button:hover::before {
      left: 100%;
    }

    .kuhul-button:hover {
      transform: translateY(-5px);
      box-shadow: 0 10px 25px rgba(0, 170, 255, 0.4);
    }

    .kuhul-button:active {
      transform: translateY(2px);
    }

    .kuhul-button.tilt-left {
      background: linear-gradient(135deg, 
        rgba(255, 107, 107, 0.8), 
        rgba(255, 142, 142, 0.8));
    }

    .kuhul-button.tilt-right {
      background: linear-gradient(135deg, 
        rgba(157, 78, 221, 0.8), 
        rgba(199, 125, 255, 0.8));
    }

    .kuhul-button.swap {
      background: linear-gradient(135deg, 
        rgba(22, 242, 170, 0.8), 
        rgba(0, 245, 255, 0.8));
      grid-column: span 2;
    }

    .swap-instructions {
      background: rgba(20, 25, 60, 0.8);
      padding: 20px;
      border-radius: 15px;
      border: 1px solid rgba(0, 229, 255, 0.3);
      margin-top: 20px;
    }

    .instructions-title {
      color: var(--π-accent);
      margin-bottom: 15px;
      font-size: 1.2rem;
      display: flex;
      align-items: center;
      gap: 10px;
    }

    .instruction {
      display: flex;
      align-items: center;
      gap: 10px;
      margin-bottom: 10px;
      padding: 8px;
      background: rgba(0, 0, 0, 0.3);
      border-radius: 8px;
    }

    .instruction-icon {
      color: var(--φ-accent);
      font-size: 1.2rem;
      min-width: 30px;
      text-align: center;
    }

    .game-overlay {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.9);
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      border-radius: 20px;
      z-index: 100;
      display: none;
    }

    .game-overlay h2 {
      font-size: 4rem;
      background: linear-gradient(45deg, #ff5555, #ff8e8e);
      -webkit-background-clip: text;
      background-clip: text;
      color: transparent;
      margin-bottom: 30px;
      text-shadow: 0 0 30px rgba(255, 85, 85, 0.5);
    }

    .combo-display {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      font-size: 4rem;
      font-weight: 900;
      color: var(--π-warning);
      text-shadow: 
        0 0 20px var(--π-warning),
        0 0 40px var(--π-warning),
        0 0 60px var(--π-warning);
      z-index: 50;
      opacity: 0;
      pointer-events: none;
      animation: π-pulse 0.5s ease-out;
    }

    @keyframes π-pulse {
      0% { transform: translate(-50%, -50%) scale(0.5); opacity: 0; }
      50% { transform: translate(-50%, -50%) scale(1.2); opacity: 1; }
      100% { transform: translate(-50%, -50%) scale(1); opacity: 0; }
    }

    .footer {
      text-align: center;
      padding: 20px;
      background: rgba(10, 15, 40, 0.9);
      border-top: 2px solid var(--π-accent);
      margin-top: auto;
    }

    .kuhul-version {
      font-family: 'SF Mono', monospace;
      opacity: 0.6;
      font-size: 0.9rem;
    }

    .kuhul-version::before {
      content: 'π/';
      color: var(--π-accent);
    }
    </style>
</head>
<body>
    <div class="kuhul-header">
        <h1 class="title">π-ATOMIX</h1>
        <p class="subtitle">KUHUL Glyph Physics Engine vπ/1.618</p>
    </div>

    <div class="game-container">
        <div class="game-board">
            <canvas id="gameCanvas" width="800" height="600"></canvas>
            <div class="game-overlay" id="gameOverScreen">
                <h2>⭮ GAME OVER ⭮</h2>
                <div class="stat-box" style="width: 300px; margin: 20px;">
                    <div class="stat-label">Final Score</div>
                    <div class="stat-value" id="finalScore">0</div>
                </div>
                <button class="kuhul-button swap" id="restartBtn">
                    ⭮ RESTART GAME
                </button>
            </div>
            <div class="combo-display" id="comboDisplay">π-BONUS!</div>
        </div>

        <div class="controls-panel">
            <div class="kuhul-stats">
                <div class="stat-box">
                    <div class="stat-label">Score</div>
                    <div class="stat-value" id="scoreValue">0</div>
                </div>
                <div class="stat-box">
                    <div class="stat-label">Level</div>
                    <div class="stat-value" id="levelValue">1</div>
                </div>
                <div class="stat-box">
                    <div class="stat-label">Cleared</div>
