            <div class="tilt-controls">
                <div class="tilt-title">
                    <span>⤸ TILT CONTROL</span>
                </div>

                <div style="display: flex; justify-content: space-between; margin-bottom: 10px;">
                    <span>Tilt Power</span>
                    <span id="tiltPercent">0%</span>
                </div>

                <div class="tilt-meter">
                    <div class="tilt-fill" id="tiltFill"></div>
                </div>

                <div class="tilt-buttons">
                    <button class="kuhul-button tilt-left" id="tiltLeftBtn">
                        ⤸ TILT LEFT
                    </button>
                    <button class="kuhul-button tilt-right" id="tiltRightBtn">
                        ⤸ TILT RIGHT
                    </button>
                    <button class="kuhul-button swap" id="swapBtn">
                        ⇆ SWAP COLUMNS
                    </button>
                </div>

                <div class="swap-instructions">
                    <div class="instructions-title">
                        <span>🎮 HOW TO PLAY</span>
                    </div>
                    <div class="instruction">
                        <span class="instruction-icon">🧱</span>
                        <span>Click glyphs to select, click adjacent column to swap</span>
                    </div>
                    <div class="instruction">
                        <span class="instruction-icon">⤸</span>
                        <span>Tilt buttons influence glyph fall direction</span>
                    </div>
                    <div class="instruction">
                        <span class="instruction-icon">π</span>
                        <span>Match 4+ glyphs for π-bonus multipliers</span>
                    </div>
                    <div class="instruction">
                        <span class="instruction-icon">⚡</span>
                        <span>Create chain reactions for φ-golden combos</span>
                    </div>
                </div>
            </div>

            <div style="display: flex; gap: 15px;">
                <button class="kuhul-button" id="pauseBtn">
                    ⏸ PAUSE
                </button>
                <button class="kuhul-button tilt-left" id="hintBtn">
                    💡 HINT
                </button>
            </div>
        </div>
    </div>

    <div class="footer">
        <p class="kuhul-version">1.618 | KUHUL π-Engine | Three-File System</p>
        <p style="opacity: 0.5; margin-top: 5px; font-size: 0.8rem;">
            Physics: <span id="physicsDebug">Calculating π...</span>
        </p>
    </div>

    <!-- KUHUL π ENGINE -->
    <script>
    // ===== KUHUL π ENGINE IMPLEMENTATION =====
    class KuhulπEngine {
        constructor() {
            console.log('⚛️ Initializing KUHUL π Engine...');

            // π Constants
            this.π = Math.PI;
            this.φ = 1.618033988749895;
            this.τ = this.π * 2;
            this.e = Math.E;

            // Game state
            this.board = null;
            this.score = 0;
            this.level = 1;
            this.cleared = 0;
            this.combo = 1.0;
            this.gameOver = false;
            this.paused = false;

            // Tilt system
            this.tiltPower = 0;
            this.maxTiltPower = 100;
            this.tiltRechargeRate = this.π / 10;
            this.currentTilt = 0; // -1 to 1 (left to right)
            this.selectedColumn = -1;

            // Physics
            this.fallAcceleration = 9.8;
            this.baseFallSpeed = 2.0;
            this.currentFallSpeed = 2.0;
            this.tiltSensitivity = this.π / 180;

            // Glyphs
            this.glyphs = [
                {symbol: '⚛️', color: '#ff5555', weight: this.π/2, name: 'atom'},
                {symbol: '☯', color: '#5555ff', weight: this.φ, name: 'yin_yang'},
                {symbol: '⚡', color: '#55ff55', weight: this.π, name: 'energy'},
                {symbol: '☣', color: '#aa55ff', weight: this.π*1.5, name: 'biohazard'},
                {symbol: '☢', color: '#ffff55', weight: this.τ, name: 'radioactive'}
            ];

            // Canvas setup
            this.canvas = document.getElementById('gameCanvas');
            this.ctx = this.canvas.getContext('2d');
            this.columns = 8;
            this.rows = 13;
            this.cellSize = 50;
            this.glyphScale = 0.8;

            // Game board (columns x rows)
            this.grid = Array(this.columns).fill().map(() => 
                Array(this.rows).fill(null)
            );

            // Falling glyphs
            this.fallingGlyphs = [];
            this.swappingGlyphs = [];
            this.particles = [];

            this.init();
        }

        // Initialize game
        init() {
            console.log('⟁ Starting π-Atomix Game...');

            // Create initial glyphs
            for (let i = 0; i < 20; i++) {
                this.addRandomGlyph();
            }

            // Apply gravity to settle
            this.applyGravity();

            // Update UI
            this.updateUI();

            // Start game loop
            this.lastTime = 0;
            this.gameLoop(0);

            // Start tilt recharge
            setInterval(() => this.rechargeTilt(), 100);
        }

        // π-based math utilities
        πSin(t) { return Math.sin(t * this.π); }
        πCos(t) { return Math.cos(t * this.π); }
        πWave(t, frequency = 1, amplitude = 1) {
            return Math.sin(t * this.π * frequency) * amplitude;
        }
        πDistance(x1, y1, x2, y2) {
            return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
        }
        πInterpolate(start, end, t, easing = 'π') {
            let easedT = t;
            if (easing === 'π') {
                easedT = 0.5 - 0.5 * Math.cos(t * this.π);
            } else if (easing === 'φ') {
                easedT = t < 0.5 ? 
                    2 * t * t : 
                    1 - Math.pow(-2 * t + 2, 2) / 2;
            }
            return start + (end - start) * easedT;
        }

        // Create a glyph with π properties
        createGlyph() {
            const type = Math.floor(Math.random() * this.glyphs.length);
            const glyph = this.glyphs[type];

            return {
                id: Date.now() + Math.random(),
                type: type,
                symbol: glyph.symbol,
                color: glyph.color,
                name: glyph.name,
                weight: glyph.weight,

                // Position
                x: 0,
                y: 0,
                targetX: 0,
                targetY: 0,

                // Physics
                velocity: { x: 0, y: 0 },
                acceleration: { x: 0, y: this.fallAcceleration },
                rotation: Math.random() * this.τ,
                rotationSpeed: (Math.random() - 0.5) * 0.1,

                // State
                falling: true,
                swapping: false,
                selected: false,
                alpha: 1,

                // Animation
                scale: 1,
                pulse: 0,
                glow: 0
            };
        }

        // Add random glyph to board
        addRandomGlyph() {
            const glyph = this.createGlyph();
            const col = Math.floor(Math.random() * this.columns);

            // Find lowest empty row
            let row = this.rows - 1;
            while (row >= 0 && this.grid[col][row] !== null) {
                row--;
            }

            if (row >= 0) {
                glyph.x = col;
                glyph.y = -1; // Start above board
                glyph.targetX = col;
                glyph.targetY = row;
                glyph.falling = true;

                this.fallingGlyphs.push(glyph);
            }
        }

        // Apply tilt to falling glyphs
        applyTilt(tiltDirection) {
            if (this.tiltPower < 10) return;

            const tiltForce = tiltDirection * Math.min(20, this.tiltPower / 5);
            this.tiltPower = Math.max(0, this.tiltPower - 10);

            // Apply tilt to all falling glyphs
            this.fallingGlyphs.forEach(glyph => {
                glyph.velocity.x += tiltForce * this.tiltSensitivity;
                glyph.velocity.x = Math.max(-2, Math.min(2, glyph.velocity.x));

                // Add visual effect
                glyph.glow = 1.0;
            });

            // Update UI
            this.updateTiltMeter();
        }

        // Recharge tilt power
        rechargeTilt() {
            if (this.tiltPower < this.maxTiltPower) {
                this.tiltPower = Math.min(
                    this.maxTiltPower,
                    this.tiltPower + this.tiltRechargeRate
                );
                this.updateTiltMeter();
            }
        }

        // Select column for swapping
        selectColumn(col) {
            if (this.selectedColumn === -1) {
                // First selection
                this.selectedColumn = col;

                // Highlight column
                for (let row = 0; row < this.rows; row++) {
                    const glyph = this.grid[col][row];
                    if (glyph) {
                        glyph.selected = true;
                        glyph.pulse = 1.0;
                    }
                }
            } else {
                // Second selection - perform swap
                if (Math.abs(this.selectedColumn - col) === 1) {
                    this.swapColumns(this.selectedColumn, col);
                }

                // Clear selection
                for (let row = 0; row < this.rows; row++) {
                    const glyph = this.grid[this.selectedColumn][row];
                    if (glyph) glyph.selected = false;
                }

                this.selectedColumn = -1;
            }
        }

        // Swap two adjacent columns
        swapColumns(col1, col2) {
            if (col1 < 0 || col2 < 0 || col1 >= this.columns || col2 >= this.columns) return;
            if (Math.abs(col1 - col2) !== 1) return;

            console.log(`⇆ Swapping columns ${col1} ↔ ${col2}`);

            // Swap grid positions
            for (let row = 0; row < this.rows; row++) {
                const temp = this.grid[col1][row];
                this.grid[col1][row] = this.grid[col2][row];
                this.grid[col2][row] = temp;

                // Update glyph positions with animation
                if (this.grid[col1][row]) {
                    this.grid[col1][row].x = col1;
                    this.grid[col1][row].targetX = col1;
                    this.grid[col1][row].swapping = true;
                    this.swappingGlyphs.push(this.grid[col1][row]);
                }

                if (this.grid[col2][row]) {
                    this.grid[col2][row].x = col2;
                    this.grid[col2][row].targetX = col2;
                    this.grid[col2][row].swapping = true;
                    this.swappingGlyphs.push(this.grid[col2][row]);
                }
            }

            // Check for matches after swap
            setTimeout(() => this.checkMatches(), 300);
        }

        // Check for matches with π pattern detection
        checkMatches() {
            let matches = [];

            // Check horizontal matches
            for (let row = 0; row < this.rows; row++) {
                for (let col = 0; col < this.columns - 3; col++) {
                    const glyph = this.grid[col][row];
                    if (!glyph) continue;

                    let matchLength = 1;
                    for (let c = col + 1; c < this.columns; c++) {
                        if (this.grid[c][row] && this.grid[c][row].type === glyph.type) {
                            matchLength++;
                        } else {
                            break;
                        }
                    }

                    if (matchLength >= 4) {
                        matches.push({
                            start: {col, row},
                            length: matchLength,
                            direction: 'horizontal',
                            type: glyph.type
                        });
                        col += matchLength - 1;
                    }
                }
            }

            // Check vertical matches
            for (let col = 0; col < this.columns; col++) {
                for (let row = 0; row < this.rows - 3; row++) {
                    const glyph = this.grid[col][row];
                    if (!glyph) continue;

                    let matchLength = 1;
                    for (let r = row + 1; r < this.rows; r++) {
                        if (this.grid[col][r] && this.grid[col][r].type === glyph.type) {
                            matchLength++;
                        } else {
                            break;
                        }
                    }

                    if (matchLength >= 4) {
                        matches.push({
                            start: {col, row},
                            length: matchLength,
                            direction: 'vertical',
                            type: glyph.type
                        });
                        row += matchLength - 1;
                    }
                }
            }

            // Clear matches if found
            if (matches.length > 0) {
                this.clearMatches(matches);
                return true;
            }

            return false;
        }

        // Clear matched glyphs with π effects
        clearMatches(matches) {
            let totalCleared = 0;
            let maxMatch = 0;

            matches.forEach(match => {
                const {start, length, direction} = match;
                totalCleared += length;
                maxMatch = Math.max(maxMatch, length);

                // Calculate π-bonus
                let baseScore = 100;
                if (length === 4) baseScore = 100;
                else if (length === 5) baseScore = 250;
                else if (length >= 6) baseScore = 500;

                // Apply π and φ multipliers
                const πBonus = Math.floor(baseScore * (length / 4) * this.π);
                const φBonus = Math.floor(πBonus * this.φ);
                const finalScore = Math.floor(φBonus * this.combo * this.level);

                this.score += finalScore;
                this.cleared += length;

                // Show combo text for big matches
                if (length >= 6) {
                    this.showComboText('π-BONUS!');
                }

                // Clear glyphs with particle effects
                if (direction === 'horizontal') {
                    for (let i = 0; i < length; i++) {
                        const col = start.col + i;
                        const row = start.row;
                        this.createParticles(col, row, match.type);
                        this.grid[col][row] = null;
                    }
                } else {
                    for (let i = 0; i < length; i++) {
                        const col = start.col;
                        const row = start.row + i;
                        this.createParticles(col, row, match.type);
                        this.grid[col][row] = null;
                    }
                }

                // Increase combo multiplier
                this.combo += 0.2 * this.φ;
            });

            // Level up every 50 cleared
            if (this.cleared >= this.level * 50) {
                this.level++;
                this.currentFallSpeed = Math.min(6.0, this.baseFallSpeed + (this.level - 1) * 0.3);
                this.showComboText('LEVEL UP!');
            }

            // Apply gravity after clearing
            this.applyGravity();

            // Update UI
            this.updateUI();

            // Check for chain reactions
            setTimeout(() => {
                if (this.checkMatches()) {
                    this.combo += 0.5 * this.φ;
                } else {
                    // Reset combo after chain ends
                    setTimeout(() => {
                        this.combo = Math.max(1.0, this.combo * 0.8);
                        this.updateUI();
                    }, 1000);
                }
            }, 500);
        }

        // Apply gravity to make glyphs fall
        applyGravity() {
            let moved = false;

            for (let col = 0; col < this.columns; col++) {
                let emptySpots = 0;

                // Scan from bottom to top
                for (let row = this.rows - 1; row >= 0; row--) {
                    if (this.grid[col][row] === null) {
                        emptySpots++;
                    } else if (emptySpots > 0) {
                        // Move glyph down
                        const glyph = this.grid[col][row];
                        this.grid[col][row + emptySpots] = glyph;
                        this.grid[col][row] = null;

                        glyph.targetY = row + emptySpots;
                        glyph.falling = true;
                        glyph.velocity.y = 0;

                        if (!this.fallingGlyphs.includes(glyph)) {
                            this.fallingGlyphs.push(glyph);
                        }

                        moved = true;
                    }
                }
            }

            return moved;
        }

        // Create particle effects
        createParticles(col, row, type) {
            const x = col * this.cellSize + this.cellSize / 2;
            const y = row * this.cellSize + this.cellSize / 2;
            const color = this.glyphs[type].color;
            const count = Math.floor(Math.random() * 10) + 15;

            for (let i = 0; i < count; i++) {
                const angle = Math.random() * this.τ;
                const speed = 2 + Math.random() * 4;
                const size = 2 + Math.random() * 6;

                this.particles.push({
                    x, y,
                    vx: Math.cos(angle) * speed,
                    vy: Math.sin(angle) * speed,
                    color,
                    size,
                    life: 1.0,
                    decay: 0.02 + Math.random() * 0.03
                });
            }
        }

        // Update falling glyphs
        updateFallingGlyphs(deltaTime) {
            for (let i = this.fallingGlyphs.length - 1; i >= 0; i--) {
                const glyph = this.fallingGlyphs[i];

                // Apply physics
                glyph.velocity.y += glyph.acceleration.y * deltaTime;
                glyph.x += glyph.velocity.x * deltaTime;
                glyph.y += glyph.velocity.y * deltaTime;

                // Apply tilt influence
                glyph.velocity.x += this.currentTilt * this.tiltSensitivity * deltaTime;
                glyph.velocity.x *= 0.95; // Damping

                // Check if reached target
                const targetX = glyph.targetX * this.cellSize + this.cellSize / 2;
                const targetY = glyph.targetY * this.cellSize + this.cellSize / 2;
                const distance = this.πDistance(glyph.x, glyph.y, targetX, targetY);

                if (distance < 5) {
                    // Landed
                    glyph.x = targetX;
                    glyph.y = targetY;
                    glyph.velocity.x = 0;
                    glyph.velocity.y = 0;
                    glyph.falling = false;

                    // Place in grid
                    this.grid[glyph.targetX][glyph.targetY] = glyph;

                    // Remove from falling list
                    this.fallingGlyphs.splice(i, 1);
                }

                // Update rotation
                glyph.rotation += glyph.rotationSpeed * deltaTime;

                // Update animations
                if (glyph.glow > 0) glyph.glow -= 0.1 * deltaTime;
                if (glyph.pulse > 0) glyph.pulse -= 0.1 * deltaTime;
            }

            // Update swapping glyphs
            for (let i = this.swappingGlyphs.length - 1; i >= 0; i--) {
                const glyph = this.swappingGlyphs[i];
                const targetX = glyph.targetX * this.cellSize + this.cellSize / 2;

                // Animate swap
                glyph.x = this.πInterpolate(glyph.x, targetX, 0.2, 'φ');

                if (Math.abs(glyph.x - targetX) < 2) {
                    glyph.x = targetX;
                    glyph.swapping = false;
                    this.swappingGlyphs.splice(i, 1);
                }
            }

            // Update particles
            for (let i = this.particles.length - 1; i >= 0; i--) {
                const p = this.particles[i];
                p.x += p.vx * deltaTime;
                p.y += p.vy * deltaTime;
                p.vy += 0.1 * deltaTime; // Gravity
                p.life -= p.decay * deltaTime;

                if (p.life <= 0) {
                    this.particles.splice(i, 1);
                }
            }
        }

        // Show combo text
        showComboText(text) {
            const display = document.getElementById('comboDisplay');
            display.textContent = text;
            display.style.opacity = '1';

            setTimeout(() => {
                display.style.opacity = '0';
            }, 1000);
        }

        // Update UI
        updateUI() {
            document.getElementById('scoreValue').textContent = Math.floor(this.score);
            document.getElementById('levelValue').textContent = this.level;
            document.getElementById('clearedValue').textContent = this.cleared;
            document.getElementById('comboValue').textContent = this.combo.toFixed(1);

            // Update physics debug
            document.getElementById('physicsDebug').textContent = 
                `Fall: ${this.currentFallSpeed.toFixed(1)} | Tilt: ${this.tiltPower.toFixed(0)}%`;
        }

        // Update tilt meter
        updateTiltMeter() {
            const percent = (this.tiltPower / this.maxTiltPower) * 100;
            document.getElementById('tiltPercent').textContent = `${Math.floor(percent)}%`;
            document.getElementById('tiltFill').style.width = `${percent}%`;
        }

        // Draw game
        draw() {
            const ctx = this.ctx;
            const width = this.canvas.width;
            const height = this.canvas.height;

            // Clear canvas with gradient
            const gradient = ctx.createLinearGradient(0, 0, width, height);
            gradient.addColorStop(0, 'rgba(10, 10, 42, 0.9)');
            gradient.addColorStop(1, 'rgba(5, 5, 25, 0.9)');
            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);

            // Draw grid
            ctx.strokeStyle = 'rgba(0, 229, 255, 0.1)';
            ctx.lineWidth = 1;

            // Vertical lines
            for (let col = 0; col <= this.columns; col++) {
                ctx.beginPath();
                ctx.moveTo(col * this.cellSize, 0);
                ctx.lineTo(col * this.cellSize, height);
                ctx.stroke();
            }

            // Horizontal lines
            for (let row = 0; row <= this.rows; row++) {
                ctx.beginPath();
                ctx.moveTo(0, row * this.cellSize);
                ctx.lineTo(width, row * this.cellSize);
                ctx.stroke();
            }

            // Draw glyphs on grid
            for (let col = 0; col < this.columns; col++) {
                for (let row = 0; row < this.rows; row++) {
                    const glyph = this.grid[col][row];
                    if (glyph && !glyph.falling) {
                        this.drawGlyph(glyph);
                    }
                }
            }

            // Draw falling glyphs
            this.fallingGlyphs.forEach(glyph => this.drawGlyph(glyph));
            this.swappingGlyphs.forEach(glyph => this.drawGlyph(glyph));

            // Draw particles
            this.particles.forEach(particle => {
                ctx.globalAlpha = particle.life;
                ctx.fillStyle = particle.color;
                ctx.beginPath();
                ctx.arc(particle.x, particle.y, particle.size, 0, this.τ);
                ctx.fill();
            });
            ctx.globalAlpha = 1;

            // Draw selected column highlight
            if (this.selectedColumn >= 0) {
                ctx.fillStyle = 'rgba(0, 229, 255, 0.1)';
                ctx.fillRect(
                    this.selectedColumn * this.cellSize,
                    0,
                    this.cellSize,
                    height
                );
            }

            // Draw game over overlay
            if (this.gameOver) {
                ctx.fillStyle = 'rgba(0, 0, 0, 0.85)';
                ctx.fillRect(0, 0, width, height);

                ctx.fillStyle = '#ff5555';
                ctx.font = '60px Arial';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText('GAME OVER', width / 2, height / 2 - 50);

                ctx.fillStyle = '#00ffaa';
                ctx.font = '30px Arial';
                ctx.fillText(`Final Score: ${this.score}`, width / 2, height / 2 + 20);
            }
        }

        // Draw individual glyph
        drawGlyph(glyph) {
            const ctx = this.ctx;
            const centerX = glyph.x || (glyph.targetX * this.cellSize + this.cellSize / 2);
            const centerY = glyph.y || (glyph.targetY * this.cellSize + this.cellSize / 2);
            const radius = (this.cellSize / 2) * this.glyphScale * glyph.scale;

            ctx.save();
            ctx.translate(centerX, centerY);
            ctx.rotate(glyph.rotation);

            // Draw glow if active
            if (glyph.glow > 0) {
                ctx.shadowColor = glyph.color;
                ctx.shadowBlur = 20 * glyph.glow;
            }

            if (glyph.selected) {
                // Pulsing selection ring
                const pulseRadius = radius * (1 + glyph.pulse * 0.2);
                ctx.strokeStyle = 'rgba(0, 229, 255, 0.8)';
                ctx.lineWidth = 3;
                ctx.beginPath();
                ctx.arc(0, 0, pulseRadius, 0, this.τ);
                ctx.stroke();
            }

            // Draw main circle
            ctx.fillStyle = glyph.color;
            ctx.beginPath();
            ctx.arc(0, 0, radius, 0, this.τ);
            ctx.fill();

            // Draw atomic rings
            ctx.strokeStyle = 'rgba(255, 255, 255, 0.7)';
            ctx.lineWidth = 2;

            // Outer ring
            ctx.beginPath();
            ctx.arc(0, 0, radius - 2, 0, this.τ);
            ctx.stroke();

            // Inner ring
            ctx.beginPath();
            ctx.arc(0, 0, radius / 2, 0, this.τ);
            ctx.stroke();

            // Draw symbol
            ctx.fillStyle = 'white';
            ctx.font = `${radius * 1.2}px Arial`;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillText(glyph.symbol, 0, 0);

            ctx.restore();
            ctx.shadowBlur = 0;
        }

        // Game loop
        gameLoop(timestamp) {
            if (!this.lastTime) this.lastTime = timestamp;
            const deltaTime = Math.min((timestamp - this.lastTime) / 16.67, 2); // Cap at 2x

            if (!this.paused && !this.gameOver) {
                this.updateFallingGlyphs(deltaTime);

                // Add new glyph periodically
                if (Math.random() < 0.02 * deltaTime) {
                    this.addRandomGlyph();
                }

                // Check for game over
                this.checkGameOver();
            }

            this.draw();
            this.lastTime = timestamp;
            requestAnimationFrame((t) => this.gameLoop(t));
        }

        // Check game over condition
        checkGameOver() {
            // Check if any column is full
            for (let col = 0; col < this.columns; col++) {
                if (this.grid[col][0] !== null) {
                    this.gameOver = true;
                    document.getElementById('finalScore').textContent = this.score;
                    document.getElementById('gameOverScreen').style.display = 'flex';
                    return;
                }
            }
        }
    }

    // ===== JAVASCRIPT AS BUTTON GUY =====
    document.addEventListener('DOMContentLoaded', () => {
        console.log('🤖 Button Guy initializing π-Atomix...');

        // Create KUHUL π Engine
        const game = new KuhulπEngine();

        // Button connections
        document.getElementById('tiltLeftBtn').addEventListener('click', () => {
            game.applyTilt(-1);
        });

        document.getElementById('tiltRightBtn').addEventListener('click', () => {
            game.applyTilt(1);
        });

        document.getElementById('swapBtn').addEventListener('click', () => {
            // Toggle swap mode
            const canvas = document.getElementById('gameCanvas');
            canvas.style.cursor = 'crosshair';

            const clickHandler = (e) => {
                const rect = canvas.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                const col = Math.floor(x / 50);
                const row = Math.floor(y / 50);

                if (col >= 0 && col < game.columns && row >= 0 && row < game.rows) {
                    game.selectColumn(col);
                }

                // Remove handler after two clicks
                if (game.selectedColumn === -1) {
                    canvas.removeEventListener('click', clickHandler);
                    canvas.style.cursor = 'default';
                }
            };

            canvas.addEventListener('click', clickHandler);
        });

        document.getElementById('pauseBtn').addEventListener('click', () => {
            game.paused = !game.paused;
            document.getElementById('pauseBtn').textContent = 
                game.paused ? '▶ RESUME' : '⏸ PAUSE';
        });

        document.getElementById('hintBtn').addEventListener('click', () => {
            // Show hint by flashing possible matches
            game.checkMatches();
        });

        document.getElementById('restartBtn').addEventListener('click', () => {
            location.reload();
        });

        // Device tilt detection
        if (window.DeviceOrientationEvent) {
            window.addEventListener('deviceorientation', (e) => {
                // Use beta (front-to-back tilt) for game physics
                const tilt = e.beta ? Math.max(-45, Math.min(45, e.beta)) / 45 : 0;
                game.currentTilt = tilt;

                // Auto-apply tilt if device is tilted enough
                if (Math.abs(tilt) > 0.3 && game.tiltPower > 20) {
                    game.applyTilt(tilt > 0 ? 1 : -1);
                }
            });
        }

        console.log('🎮 π-Atomix Ready!');
        console.log('🧱 Game Engine: KUHUL π');
        console.log('⚛️ Glyphs: 5 types with π-physics');
        console.log('⤸ Tilt System: Power-based with recharge');
        console.log('⇆ Column Swapping: Adjacent column swap mechanic');
