// runtime/trinity-orchestrator.js
// HOLY TRINITY RUNTIME ORCHESTRATOR
// CPU (ASXR v1.x) + GPU (ASXR-GPU v4.x) + TPU (ASX TPU-OS / QLoRA)

export class TrinityOrchestrator {
  constructor(options = {}) {
    this.endpoints = {
      cpu: options.cpu || {
        base: "",
        status: "/xjson/status",
        run: "/xjson/run",
        execute: "/xjson/execute"
      },
      gpu: options.gpu || {
        base: "",
        health: "/api/gpu/health",
        kuhulExecute: "/api/kuhul/execute",
        neuralProcess: "/api/neural/process",
        vectorCompress: "/api/vector/compress",
      },
      tpu: options.tpu || {
        base: "",
        health: "/trainer/health",
        jobs: "/trainer/jobs",
        job: (id) => `/trainer/jobs/${id}`,
        datasets: "/trainer/datasets",
      },
    };

    this.capabilities = {
      cpu: false,
      gpu: false,
      tpu: false,
    };

    this.stats = {
      cpuOps: 0,
      gpuOps: 0,
      tpuOps: 0,
      lastOperation: null
    };
  }

  // ---- Detection / health checks ---------------------------------

  async detectAll() {
    const results = await Promise.allSettled([
      this.detectCPU(),
      this.detectGPU(),
      this.detectTPU(),
    ]);

    console.log('🔍 Trinity Detection Results:', {
      cpu: this.capabilities.cpu,
      gpu: this.capabilities.gpu,
      tpu: this.capabilities.tpu
    });

    return this.capabilities;
  }

  async detectCPU() {
    try {
      const res = await fetch(this.endpoints.cpu.status);
      this.capabilities.cpu = res.ok;
      if (res.ok) {
        const data = await res.json();
        console.log('✅ CPU Runtime:', data);
      }
    } catch (error) {
      console.warn('❌ CPU Runtime unavailable:', error.message);
      this.capabilities.cpu = false;
    }
    return this.capabilities.cpu;
  }

  async detectGPU() {
    try {
      const res = await fetch(this.endpoints.gpu.health);
      this.capabilities.gpu = res.ok;
      if (res.ok) {
        const data = await res.json();
        console.log('✅ GPU Runtime:', data);
      }
    } catch (error) {
      console.warn('❌ GPU Runtime unavailable:', error.message);
      this.capabilities.gpu = false;
    }
    return this.capabilities.gpu;
  }

  async detectTPU() {
    try {
      const res = await fetch(this.endpoints.tpu.health);
      this.capabilities.tpu = res.ok;
      if (res.ok) {
        const data = await res.json();
        console.log('✅ TPU Runtime:', data);
      }
    } catch (error) {
      console.warn('❌ TPU Runtime unavailable:', error.message);
      this.capabilities.tpu = false;
    }
    return this.capabilities.tpu;
  }

  // ---- High-level task API ---------------------------------------

  /**
   * Run a task on appropriate shard.
   * type: "logic" | "render" | "train" | "mixed" | "auto"
   * payload: arbitrary JSON (glyphs, XJSON, job spec, etc.)
   */
  async runTask({ type, payload, priority = "normal" }) {
    this.stats.lastOperation = { type, timestamp: Date.now() };

    // Auto-detect best shard based on operation type
    if (type === "auto") {
      type = this.autoDetectTaskType(payload);
    }

    switch (type) {
      case "logic":
        this.stats.cpuOps++;
        return this.runCPU(payload);
      case "render":
        this.stats.gpuOps++;
        return this.runGPU(payload);
      case "train":
        this.stats.tpuOps++;
        return this.runTPU(payload);
      case "mixed":
        return this.runPipeline(payload);
      default:
        throw new Error(`Unknown task type: ${type}`);
    }
  }

  autoDetectTaskType(payload) {
    if (payload.operation && this.isGPUOperation(payload.operation)) {
      return "render";
    }
    if (payload.jobType && payload.jobType === "train") {
      return "train";
    }
    if (payload.glyphs && payload.glyphs.includes('GPU ') || payload.glyphs.includes('Trainer ')) {
      return "mixed";
    }
    return "logic";
  }

  isGPUOperation(operation) {
    const gpuOps = ['(⤍)', '(⤎)', '(↻)', '(⟿)', '(⟲)', '(⤦)', '(⤧)', '(⤨)'];
    return gpuOps.includes(operation);
  }

  // ---- CPU shard (ASXR v1.x + XJSON) -----------------------------

  async runCPU(payload) {
    if (!this.capabilities.cpu) {
      await this.detectCPU();
      if (!this.capabilities.cpu) {
        throw new Error("CPU runtime unavailable - ASXR v1.x not running");
      }
    }

    const res = await fetch(this.endpoints.cpu.run, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    if (!res.ok) {
      const errorText = await res.text();
      throw new Error(`CPU run failed: ${res.status} - ${errorText}`);
    }

    return res.json();
  }

  // ---- GPU shard (ASXR-GPU) --------------------------------------

  /**
   * payload = {
   *   operation: "(⤍)" | "(⟿)" | "(↻)" | "(⟲)" | ...
   *   args: [...]
   * }
   */
  async runGPU(payload) {
    if (!this.capabilities.gpu) {
      await this.detectGPU();
      if (!this.capabilities.gpu) {
        throw new Error("GPU runtime unavailable - ASXR-GPU v4.x not running");
      }
    }

    const res = await fetch(this.endpoints.gpu.kuhulExecute, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        operation: payload.operation,
        args: payload.args || [],
        metadata: payload.metadata || {}
      }),
    });

    if (!res.ok) {
      const errorText = await res.text();
      throw new Error(`GPU run failed: ${res.status} - ${errorText}`);
    }

    return res.json();
  }

  // ---- TPU shard (ASX TPU-OS / QLoRA) ----------------------------

  /**
   * payload = {
   *   jobType: "train" | "eval" | "export",
   *   model: "...",
   *   dataset: "...",
   *   params: {...}
   * }
   */
  async runTPU(payload) {
    if (!this.capabilities.tpu) {
      await this.detectTPU();
      if (!this.capabilities.tpu) {
        throw new Error("TPU runtime unavailable - ASXR-TPU not running");
      }
    }

    const res = await fetch(this.endpoints.tpu.jobs, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    if (!res.ok) {
      const errorText = await res.text();
      throw new Error(`TPU job submission failed: ${res.status} - ${errorText}`);
    }

    return res.json();
  }

  async getTPUJob(id) {
    const res = await fetch(this.endpoints.tpu.job(id));
    if (!res.ok) {
      const errorText = await res.text();
      throw new Error(`TPU job query failed: ${res.status} - ${errorText}`);
    }
    return res.json();
  }

  async getTPUDatasets() {
    const res = await fetch(this.endpoints.tpu.datasets);
    if (!res.ok) throw new Error("TPU datasets query failed");
    return res.json();
  }

  // ---- Mixed CPU→GPU→TPU pipelines -------------------------------

  /**
   * payload = {
   *   cpuStage: {...},   // e.g. XJSON generation of glyphs or dataset
   *   gpuStage: {...},   // e.g. geometry / SVG-weights gen
   *   tpuStage: {...}    // e.g. QLoRA training spec
   * }
   */
  async runPipeline(stages) {
    const result = { cpu: null, gpu: null, tpu: null, errors: [] };

    try {
      if (stages.cpuStage) {
        console.log('🖥️ Starting CPU stage...');
        result.cpu = await this.runCPU(stages.cpuStage);
        console.log('✅ CPU stage completed');
      }

      if (stages.gpuStage) {
        console.log('🎨 Starting GPU stage...');
        // Optionally feed CPU output into GPU stage
        const gpuPayload = {
          ...(stages.gpuStage || {}),
          upstream: result.cpu,
        };
        result.gpu = await this.runGPU(gpuPayload);
        console.log('✅ GPU stage completed');
      }

      if (stages.tpuStage) {
        console.log('🧠 Starting TPU stage...');
        const tpuPayload = {
          ...(stages.tpuStage || {}),
          upstream: { cpu: result.cpu, gpu: result.gpu },
        };
        result.tpu = await this.runTPU(tpuPayload);
        console.log('✅ TPU stage completed');
      }
    } catch (error) {
      result.errors.push(error.message);
      console.error('❌ Pipeline error:', error);
    }

    return result;
  }

  // ---- Monitoring and Stats --------------------------------------

  getStats() {
    return {
      ...this.stats,
      capabilities: { ...this.capabilities },
      timestamp: Date.now()
    };
  }

  async getDetailedStatus() {
    const status = {
      cpu: { available: this.capabilities.cpu },
      gpu: { available: this.capabilities.gpu },
      tpu: { available: this.capabilities.tpu },
      stats: this.getStats()
    };

    // Get detailed info from each shard if available
    if (this.capabilities.cpu) {
      try {
        const res = await fetch(this.endpoints.cpu.status);
        status.cpu.details = await res.json();
      } catch (error) {
        status.cpu.error = error.message;
      }
    }

    if (this.capabilities.gpu) {
      try {
        const res = await fetch(this.endpoints.gpu.health);
        status.gpu.details = await res.json();
      } catch (error) {
        status.gpu.error = error.message;
      }
    }

    if (this.capabilities.tpu) {
      try {
        const res = await fetch(this.endpoints.tpu.health);
        status.tpu.details = await res.json();
      } catch (error) {
        status.tpu.error = error.message;
      }
    }

    return status;
  }
}

