    return "job-" + uuid.uuid4().hex[:8]

@app.route("/trainer/health", methods=["GET"])
def trainer_health():
    """Health check endpoint"""
    return jsonify({
        "ok": True,
        "service": "ASXR-TPU Trainer",
        "version": "1.0.0",
        "backend": "QLoRA",
        "ready": True,
        "timestamp": now(),
        "stats": {
            "active_jobs": len([j for j in JOBS.values() if j["status"] in ["queued", "running"]]),
            "total_jobs": len(JOBS),
            "supported_models": len(LIST_MODELS()),
            "available_datasets": len(LIST_DATASETS())
        }
    })

@app.route("/trainer/datasets", methods=["GET"])
def trainer_datasets():
    """List available datasets"""
    return jsonify({
        "ok": True,
        "datasets": LIST_DATASETS(),
        "count": len(LIST_DATASETS())
    })

@app.route("/trainer/models", methods=["GET"])
def trainer_models():
    """List supported models"""
    return jsonify({
        "ok": True,
        "models": LIST_MODELS(),
        "count": len(LIST_MODELS())
    })

@app.route("/trainer/jobs", methods=["GET"])
def trainer_jobs_list():
    """List all jobs with filtering"""
    status_filter = request.args.get('status')

    jobs = list(JOBS.values())
    if status_filter:
        jobs = [j for j in jobs if j["status"] == status_filter]

    return jsonify({
        "ok": True,
        "jobs": jobs,
        "count": len(jobs),
        "filters": {"status": status_filter}
    })

@app.route("/trainer/jobs", methods=["POST"])
def trainer_jobs_create():
    """
    Create a new training job

    Body example:
    {
      "jobType": "train",
      "model": "Qwen/Qwen1.5-0.5B",
      "dataset": "xjson_seed",
      "params": {
        "epochs": 3,
        "learning_rate": 3e-4,
        "batch_size": 4
      },
      "upstream": {...}  # Optional data from CPU/GPU stages
    }
    """
    try:
        spec = request.get_json(force=True, silent=True) or {}

        # Validate required fields
        if not spec.get("model"):
            return jsonify({"ok": False, "error": "Missing required field: model"}), 400

        if not spec.get("dataset"):
            return jsonify({"ok": False, "error": "Missing required field: dataset"}), 400

        # Generate job ID
        job_id = generate_job_id()

        # Create job record
        job = {
            "id": job_id,
            "status": "queued",
            "created_at": now(),
            "updated_at": now(),
            "spec": spec,
            "logs": [f"Job created at {now()}"],
            "result": None,
            "progress": 0.0,
            "estimated_completion": None
        }

        # Store job
        with JOB_LOCK:
            JOBS[job_id] = job

        logger.info(f"Created new job: {job_id} for model {spec['model']}")

        # Launch training in background thread
        t = threading.Thread(target=_run_job_thread, args=(job_id,), daemon=True)
        t.start()

        return jsonify({
            "ok": True,
            "id": job_id,
            "status": job["status"],
            "message": "Job queued for execution"
        })

    except Exception as e:
        logger.error(f"Job creation failed: {str(e)}")
        return jsonify({"ok": False, "error": f"Job creation failed: {str(e)}"}), 500

@app.route("/trainer/jobs/<job_id>", methods=["GET"])
def trainer_jobs_status(job_id):
    """Get job status and results"""
    job = JOBS.get(job_id)
    if not job:
        return jsonify({"ok": False, "error": "Job not found"}), 404

    return jsonify({
        "ok": True,
        "job": job
    })

@app.route("/trainer/jobs/<job_id>/cancel", methods=["POST"])
def trainer_jobs_cancel(job_id):
    """Cancel a running job"""
    job = JOBS.get(job_id)
    if not job:
        return jsonify({"ok": False, "error": "Job not found"}), 404

    if job["status"] not in ["queued", "running"]:
        return jsonify({"ok": False, "error": "Job cannot be cancelled in current state"}), 400

    job["status"] = "cancelled"
    job["updated_at"] = now()
    job["logs"].append(f"Job cancelled at {now()}")

    return jsonify({
        "ok": True,
        "message": "Job cancellation requested",
        "job_id": job_id
    })

def _run_job_thread(job_id: str):
    """Background thread to execute training job"""
    job = JOBS[job_id]

    try:
        # Update job status
        job["status"] = "running"
        job["updated_at"] = now()
        job["logs"].append("Job execution started")

        # Progress callback
        def progress_callback(progress, message):
            job["progress"] = progress
            job["logs"].append(message)
            job["updated_at"] = now()

        # Execute training
        result, logs = run_qlora_job(
            job["spec"], 
            log_fn=lambda msg: job["logs"].append(msg),
            progress_fn=progress_callback
        )

        # Update job with results
        job["status"] = "completed"
        job["updated_at"] = now()
        job["result"] = result
        job["progress"] = 1.0
        job["logs"].extend(logs)
        job["logs"].append("Job completed successfully")

        logger.info(f"Job completed: {job_id}")

    except Exception as e:
        # Handle job failure
        job["status"] = "failed"
        job["updated_at"] = now()
        job["logs"].append(f"ERROR: {repr(e)}")
        job["result"] = {"error": str(e)}

        logger.error(f"Job failed: {job_id} - {str(e)}")

if __name__ == "__main__":
    logger.info("Starting ASXR-TPU Runtime Node...")
    print("🚀 ASXR-TPU Trainer Node starting on http://0.0.0.0:8050")
    print("📊 Health: http://localhost:8050/trainer/health")
    print("📚 Datasets: http://localhost:8050/trainer/datasets")
    print("🤖 Models: http://localhost:8050/trainer/models")

    # Bind to all interfaces, port 8050
    app.run(host="0.0.0.0", port=8050, debug=False)
