// ASXR-TPU Gateway Server
// Provides HTTP gateway to Python TPU node with enhanced features

import express from 'express';
import { createProxyMiddleware } from 'http-proxy-middleware';
import cors from 'cors';

const app = express();
const PORT = process.env.PORT || 8051;
const TPU_NODE_URL = process.env.TPU_NODE_URL || 'http://localhost:8050';

// Middleware
app.use(cors());
app.use(express.json());

// Health check with gateway status
app.get('/gateway/health', async (req, res) => {
  try {
    // Check if Python node is reachable
    const tpuHealth = await fetch(`${TPU_NODE_URL}/trainer/health`).then(r => r.json());

    res.json({
      ok: true,
      service: 'ASXR-TPU Gateway',
      version: '1.0.0',
      tpu_node: {
        reachable: true,
        status: tpuHealth
      },
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.json({
      ok: true,
      service: 'ASXR-TPU Gateway', 
      version: '1.0.0',
      tpu_node: {
        reachable: false,
        error: error.message
      },
      timestamp: new Date().toISOString()
    });
  }
});

// Enhanced job submission with validation
app.post('/gateway/jobs', async (req, res) => {
  try {
    const jobSpec = req.body;

    // Basic validation
    if (!jobSpec.model || !jobSpec.dataset) {
      return res.status(400).json({
        ok: false,
        error: 'Missing required fields: model and dataset'
      });
    }

    // Forward to TPU node
    const tpuResponse = await fetch(`${TPU_NODE_URL}/trainer/jobs`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(jobSpec)
    });

    const result = await tpuResponse.json();

    if (!tpuResponse.ok) {
      return res.status(tpuResponse.status).json(result);
    }

    // Enhance response with gateway info
    res.json({
      ...result,
      gateway: {
        id: 'asxr-tpu-gateway',
        submitted_at: new Date().toISOString()
      }
    });

  } catch (error) {
    res.status(500).json({
      ok: false,
      error: `Gateway error: ${error.message}`
    });
  }
});

// Proxy all other /trainer/* requests to Python node
app.use('/trainer', createProxyMiddleware({
  target: TPU_NODE_URL,
  changeOrigin: true,
  pathRewrite: {
    '^/trainer': '/trainer'
  },
  onError: (err, req, res) => {
    console.error('TPU Node proxy error:', err);
    res.status(502).json({
      ok: false,
      error: 'TPU node unavailable',
      message: err.message
    });
  }
}));

// Gateway-specific routes
app.get('/gateway/stats', async (req, res) => {
  try {
    const [health, jobs] = await Promise.all([
      fetch(`${TPU_NODE_URL}/trainer/health`).then(r => r.json()),
      fetch(`${TPU_NODE_URL}/trainer/jobs`).then(r => r.json())
    ]);

    res.json({
      ok: true,
      gateway: {
        uptime: process.uptime(),
        node_connected: health.ok
      },
      tpu_node: health,
      jobs: jobs.jobs || [],
      summary: {
        total_jobs: jobs.count || 0,
        active_jobs: (jobs.jobs || []).filter(j => j.status === 'running').length,
        completed_jobs: (jobs.jobs || []).filter(j => j.status === 'completed').length
      }
    });

  } catch (error) {
    res.status(500).json({
      ok: false,
      error: `Failed to gather stats: ${error.message}`
    });
  }
});

app.listen(PORT, () => {
