        </div>
      </div>

      <!-- System Status -->
      <div class="card">
        <h3>📊 System Status</h3>
        <div class="stats-grid">
          <div class="stat-item">
            <div class="stat-value" id="active-jobs">0</div>
            <div class="stat-label">Active Jobs</div>
          </div>
          <div class="stat-item">
            <div class="stat-value" id="total-jobs">0</div>
            <div class="stat-label">Total Jobs</div>
          </div>
          <div class="stat-item">
            <div class="stat-value" id="node-status">❓</div>
            <div class="stat-label">TPU Node</div>
          </div>
        </div>

        <div style="margin-top: 20px;">
          <h4>Trinity Integration</h4>
          <div id="trinity-status">Not initialized</div>
          <button onclick="initializeTrinity()" style="margin-top: 10px; width: 100%;">Initialize Trinity Orchestrator</button>
        </div>
      </div>
    </div>

    <!-- Execution Log -->
    <div class="card">
      <h3>📝 Execution Log</h3>
      <div id="log">
        <div class="log-line">
          <span class="log-time">[00:00:00]</span>
          <span>ASXR-TPU Dashboard initialized</span>
        </div>
      </div>
    </div>
  </div>

  <script type="module">
    import { TrinityOrchestrator } from './runtime/trinity-orchestrator.js';

    const logEl = document.getElementById('log');
    let currentJobId = null;
    let pollInterval = null;
    let trinityOrchestrator = null;

    // Initialize Trinity Orchestrator
    window.initializeTrinity = async function() {
      try {
        log('SYSTEM', 'Initializing Trinity Orchestrator...');

        trinityOrchestrator = new TrinityOrchestrator({
          tpu: {
            base: '',
            health: '/trainer/health',
            jobs: '/trainer/jobs',
            job: id => `/trainer/jobs/${id}`,
            datasets: '/trainer/datasets'
          }
        });

        await trinityOrchestrator.detectAll();

        const status = await trinityOrchestrator.getDetailedStatus();
        document.getElementById('trinity-status').textContent = 
          `✅ Trinity Ready - TPU: ${status.tpu.available ? '✅' : '❌'}`;

        log('SYSTEM', 'Trinity Orchestrator initialized successfully');

      } catch (error) {
        log('ERROR', `Trinity initialization failed: ${error.message}`);
        document.getElementById('trinity-status').textContent = '❌ Initialization Failed';
      }
    };

    // Submit training job
    window.submitTrainingJob = async function() {
      if (!trinityOrchestrator) {
        log('ERROR', 'Trinity Orchestrator not initialized');
        return;
      }

      const submitBtn = document.getElementById('submit-btn');
      submitBtn.disabled = true;
      submitBtn.textContent = 'Submitting...';

      try {
        const model = document.getElementById('model').value;
        const dataset = document.getElementById('dataset').value;
        const params = JSON.parse(document.getElementById('params').value || '{}');

        log('JOB', `Submitting training job: ${model} on ${dataset}`);

        const result = await trinityOrchestrator.runTPU({
          jobType: 'train',
          model,
          dataset,
          params
        });

        currentJobId = result.id;
        log('JOB', `Job submitted: ${currentJobId}`);

        // Show job status panel
        const statusPanel = document.getElementById('job-status');
        statusPanel.style.display = 'block';
        document.getElementById('job-id').textContent = currentJobId;
        document.getElementById('job-status-text').textContent = 'Queued';

        // Start polling job status
        startJobPolling();

      } catch (error) {
        log('ERROR', `Job submission failed: ${error.message}`);
      } finally {
        submitBtn.disabled = false;
        submitBtn.textContent = 'Submit Training Job';
      }
    };

    // Poll job status
    function startJobPolling() {
      if (pollInterval) clearInterval(pollInterval);

      pollInterval = setInterval(async () => {
        if (!currentJobId) return;

        try {
          const jobResult = await trinityOrchestrator.getTPUJob(currentJobId);
          const job = jobResult.job;

          document.getElementById('job-status-text').textContent = job.status;
          document.getElementById('job-progress').style.width = `${(job.progress || 0) * 100}%`;

          // Update logs
          job.logs.slice(-5).forEach(log => {
            if (!logEl.textContent.includes(log)) {
              log('JOB', log);
            }
          });

          // Stop polling if job completed
          if (['completed', 'failed', 'cancelled'].includes(job.status)) {
            clearInterval(pollInterval);
            document.getElementById('cancel-btn').style.display = 'none';
            log('JOB', `Job ${job.status}: ${currentJobId}`);
          }

        } catch (error) {
          log('ERROR', `Job status check failed: ${error.message}`);
        }
      }, 2000);
    }

    // Cancel job
    window.cancelJob = async function() {
      if (!currentJobId) return;

      try {
        await fetch(`/trainer/jobs/${currentJobId}/cancel`, { method: 'POST' });
        log('JOB', `Cancellation requested for job: ${currentJobId}`);
      } catch (error) {
        log('ERROR', `Cancel request failed: ${error.message}`);
      }
    };

    // Update system status
    async function updateSystemStatus() {
      try {
        const health = await fetch('/trainer/health').then(r => r.json());
        const jobs = await fetch('/trainer/jobs').then(r => r.json());

        document.getElementById('active-jobs').textContent = 
          health.stats?.active_jobs || 0;
        document.getElementById('total-jobs').textContent = 
          health.stats?.total_jobs || 0;
        document.getElementById('node-status').textContent = 
          health.ok ? '✅' : '❌';

      } catch (error) {
        document.getElementById('node-status').textContent = '❌';
      }
    }

    // Logging function
    function log(module, message) {
      const line = document.createElement('div');
      line.className = 'log-line';
      line.innerHTML = `<span class="log-time">[${new Date().toLocaleTimeString()}]</span> <span>${module}: ${message}</span>`;
      logEl.appendChild(line);
      logEl.scrollTop = logEl.scrollHeight;
    }

    // Initialize dashboard
    window.addEventListener('load', () => {
      log('SYSTEM', 'ASXR-TPU Dashboard loaded');
      updateSystemStatus();
      setInterval(updateSystemStatus, 10000); // Update every 10 seconds

      // Auto-initialize Trinity if TPU node is available
      setTimeout(() => initializeTrinity(), 1000);
    });

    // Global access for debugging
    window.trinityOrchestrator = trinityOrchestrator;
    window.log = log;
  </script>
</body>
</html>
