## D) K'UHUL Trinity Executor

**File:** `runtime/kuhul-trinity-executor.js`

```javascript
// runtime/kuhul-trinity-executor.js
// K'uhul Trinity Executor — dispatch glyphs to CPU / GPU / TPU

import { TrinityOrchestrator } from "./trinity-orchestrator.js";

/**
 * K'UHUL Trinity Executor
 * 
 * This class wraps an existing KuhulVM instance and transparently
 * routes operations to the appropriate shard (CPU/GPU/TPU) based on
 * glyph patterns and operation types.
 */
export class TrinityKuhulExecutor {
  constructor(baseVM, options = {}) {
    this.vm = baseVM; // Existing KuhulVM used for CPU/DOM logic
    this.orch = options.orchestrator || new TrinityOrchestrator();
    this.debug = options.debug || false;

    // GPU glyph patterns
    this.gpuGlyphs = new Set([
      '(⤍)', '(⤎)', '(⤏)', '(⤐)', // ASC Cipher
      '(↻)', '(↔)', '(⤒)', '(⤓)', // SCX Compression  
      '(⟲)', '(⤦)', '(⤧)', '(⤨)', // 3D Control Flow
      '(⟿)', '(⤂)', '(⤃)', '(⤄)'  // Neural Operations
    ]);

    // TPU command patterns
    this.tpuCommands = new Set(['trainer_submit', 'trainer_status', 'trainer_cancel']);

    // Operation routing cache
    this.routingCache = new Map();

    this.initialized = false;
  }

  /**
   * Initialize the executor and detect available shards
   */
  async initialize() {
    if (this.initialized) return;

    try {
      await this.orch.detectAll();
      this.initialized = true;

      if (this.debug) {
        console.log('🎯 TrinityKuhulExecutor initialized:', this.orch.capabilities);
      }
    } catch (error) {
      console.error('❌ TrinityKuhulExecutor initialization failed:', error);
      throw error;
    }
  }

  /**
   * Central entry point: execute a K'uhul command token
   * 
   * @param {string} token - K'uhul token like "[Wo \"text\"]", "[GPU (⤍) arg1 arg2]", "[Trainer submit ...]"
   * @returns {Promise<any>} Execution result
   */
  async executeToken(token) {
    if (!this.initialized) {
      await this.initialize();
    }

    // Non-command tokens (strings, literals) just go to VM
    if (!token.startsWith("[") || !token.endsWith("]")) {
      return this.vm.processToken(token);
    }

    const cmd = token.slice(1, -1).trim();

    // Check routing cache first
    if (this.routingCache.has(cmd)) {
      const route = this.routingCache.get(cmd);
      return this.executeWithRoute(cmd, route);
    }

    // Determine routing based on command pattern
    const route = this.determineRoute(cmd);
    this.routingCache.set(cmd, route);

    return this.executeWithRoute(cmd, route);
  }

  /**
   * Determine the appropriate shard for a command
   */
  determineRoute(cmd) {
    const lowerCmd = cmd.toLowerCase();

    // 1) Explicit GPU-prefixed commands: "GPU (⤍) arg1 arg2"
    if (lowerCmd.startsWith("gpu ")) {
      return { type: 'gpu', command: cmd.substring(4).trim() };
    }

    // 2) Explicit Trainer commands: "Trainer submit { ... }"
    if (lowerCmd.startsWith("trainer ")) {
      return { type: 'tpu', command: cmd.substring(8).trim() };
    }

    // 3) Direct glyph detection: "(⤍) foo bar"
    const firstToken = cmd.split(/\s+/)[0];
    if (this.gpuGlyphs.has(firstToken)) {
      return { type: 'gpu', command: cmd };
    }

    // 4) Special CPU operations that might benefit from GPU
    if (this.isComputeIntensive(cmd)) {
      return { type: 'gpu_fallback', command: cmd };
    }

    // 5) Default to CPU VM
    return { type: 'cpu', command: cmd };
  }

  /**
   * Execute command with determined route
   */
  async executeWithRoute(cmd, route) {
    switch (route.type) {
      case 'gpu':
        return this.handleGPUCommand(route.command);
      case 'tpu':
        return this.handleTrainerCommand(route.command);
      case 'gpu_fallback':
        // Try GPU first, fallback to CPU
        try {
          if (this.orch.capabilities.gpu) {
            return await this.handleGPUCommand(route.command);
          }
        } catch (error) {
          console.warn('GPU fallback failed, using CPU:', error.message);
        }
        // Fall through to CPU
      case 'cpu':
      default:
        return this.vm.executeCommand(cmd);
    }
  }

  /**
   * Check if command is compute-intensive and might benefit from GPU
   */
  isComputeIntensive(cmd) {
    const intensivePatterns = [
      /matrix/i, /vector/i, /transform/i, /encrypt/i, /compress/i,
      /render/i, /process/i, /calculate/i, /compute/i
    ];

    return intensivePatterns.some(pattern => pattern.test(cmd));
  }

  // ---- GPU Command Handling ----------------------------------------

  /**
   * Handle GPU commands and operations
   * 
   * @param {string} gpuCmd - GPU command like "(⤍) myData myPathKey" or "(⟿) inputVector"
   */
  async handleGPUCommand(gpuCmd) {
    if (!this.orch.capabilities.gpu) {
      throw new Error("GPU runtime unavailable for command: " + gpuCmd);
    }

    const parts = gpuCmd.split(/\s+/);
    const glyph = parts[0];
    const args = parts.slice(1);

    if (this.debug) {
      console.log('🎨 Executing GPU command:', { glyph, args });
    }

    // Prepare GPU payload
    const gpuPayload = {
      operation: glyph,
      args: args,
      metadata: {
        source: 'kuhul_executor',
        timestamp: Date.now(),
        vm_stack: this.vm.stack.slice(-5) // Last 5 stack items for context
      }
    };

    // Execute on GPU shard
    const result = await this.orch.runGPU(gpuPayload);

    // Push result onto VM stack so subsequent CPU commands can use it
    if (result && typeof result === 'object') {
      this.vm.stack.push(result);
    } else {
      this.vm.stack.push(String(result));
    }

    if (this.debug) {
      console.log('✅ GPU command completed:', result);
    }

    return result;
  }

  // ---- TPU / Trainer Command Handling ------------------------------

  /**
   * Handle Trainer commands for TPU operations
   * 
   * @param {string} trainerCmd - Trainer command like "submit {...}" or "status job-1234"
   */
  async handleTrainerCommand(trainerCmd) {
    if (!this.orch.capabilities.tpu) {
      throw new Error("TPU runtime unavailable for command: " + trainerCmd);
    }

    const [sub, ...rest] = trainerCmd.split(" ");
    const subCmd = sub.toLowerCase();
    const restStr = rest.join(" ").trim();

    if (this.debug) {
      console.log('🧠 Executing Trainer command:', { subCmd, restStr });
    }

    let result;

    try {
      switch (subCmd) {
        case "submit":
          result = await this.handleTrainerSubmit(restStr);
          break;

        case "status":
          result = await this.handleTrainerStatus(restStr);
          break;

        case "cancel":
          result = await this.handleTrainerCancel(restStr);
          break;

        case "datasets":
          result = await this.handleTrainerDatasets();
          break;

        case "models":
          result = await this.handleTrainerModels();
          break;

        default:
          throw new Error(`Unknown Trainer command: ${subCmd}`);
      }

      // Push result to VM stack
      this.vm.stack.push(result);

      if (this.debug) {
        console.log('✅ Trainer command completed:', result);
      }

      return result;

    } catch (error) {
      console.error('❌ Trainer command failed:', error);
      throw error;
    }
  }

  async handleTrainerSubmit(jsonStr) {
    let spec = {};

    try {
      spec = JSON.parse(jsonStr);
    } catch (e) {
      // Try to parse as K'uhul-style arguments
      const parts = jsonStr.split(/\s+/);
      if (parts.length >= 2) {
        spec = {
          model: parts[0],
          dataset: parts[1],
          params: parts[2] ? JSON.parse(parts[2]) : {}
        };
      } else {
        throw new Error("Trainer submit expects JSON spec or model/dataset args");
      }
    }

    // Add current VM context to upstream data
    if (!spec.upstream) {
      spec.upstream = {
        kuhul_context: {
          stack_depth: this.vm.stack.length,
          variables: Array.from(this.vm.vars.entries()),
          last_operations: this.vm.stack.slice(-3)
        }
      };
    }

    return await this.orch.runTPU({
      jobType: spec.jobType || "train",
      model: spec.model,
      dataset: spec.dataset,
      params: spec.params || {},
      upstream: spec.upstream
    });
  }

  async handleTrainerStatus(jobId) {
    if (!jobId) {
      throw new Error("Trainer status requires job id");
    }

    return await this.orch.getTPUJob(jobId);
  }

  async handleTrainerCancel(jobId) {
    if (!jobId) {
      throw new Error("Trainer cancel requires job id");
    }

    // Note: This would need to be implemented in the TPU node
    // For now, we'll update the job status locally
    console.warn('Trainer cancel not fully implemented yet');
    return { ok: true, message: `Cancel requested for job ${jobId}` };
  }

  async handleTrainerDatasets() {
    return await this.orch.getTPUDatasets();
  }

  async handleTrainerModels() {
    // This would need to be added to the orchestrator
    try {
      const res = await fetch('/trainer/models');
      return await res.json();
    } catch (error) {
      throw new Error(`Failed to fetch models: ${error.message}`);
    }
  }

  // ---- Batch Execution --------------------------------------------

  /**
   * Execute multiple K'uhul tokens with optimized routing
   */
  async executeTokens(tokens) {
    if (!this.initialized) {
      await this.initialize();
    }

    const results = [];

    for (const token of tokens) {
      try {
        const result = await this.executeToken(token);
        results.push({ token, result, success: true });
      } catch (error) {
        results.push({ token, error: error.message, success: false });

        if (this.debug) {
          console.error(`❌ Token execution failed: ${token}`, error);
        }
      }
    }

    return results;
  }

  /**
   * Execute a complete K'uhul script with mixed CPU/GPU/TPU operations
   */
  async executeScript(script) {
    const tokens = this.tokenizeScript(script);
    return await this.executeTokens(tokens);
  }

  /**
   * Tokenize a K'uhul script into executable tokens
   */
  tokenizeScript(script) {
    // Simple tokenization - in practice, use proper K'uhul parser
    return script.match(/\[[^\]]+\]|[^\s\[\]]+/g) || [];
  }

  // ---- Monitoring and Diagnostics --------------------------------

  /**
   * Get execution statistics and routing information
   */
  getStats() {
    const orchStats = this.orch.getStats();

    return {
      executor: {
        initialized: this.initialized,
        routingCacheSize: this.routingCache.size,
        debug: this.debug
      },
      routing: {
