### 2) `tpu/kuhul_tpu_node.py` – Trainer HTTP Node

This is a real Flask-based trainer node exposing `/trainer/*` endpoints used by the orchestrator and HIVE REST Map.

```python
# tpu/kuhul_tpu_node.py
"""
ASXR-TPU Runtime Node
- Exposes trainer REST API
- Wraps QLoRA training pipeline
"""

from flask import Flask, request, jsonify
from datetime import datetime
import threading
import uuid
import time

from qlora_trainer import run_qlora_job, LIST_DATASETS

app = Flask(__name__)

JOBS = {}  # id -> {status, created_at, updated_at, spec, logs, result}

def now():
    return datetime.utcnow().isoformat() + "Z"


@app.route("/trainer/health", methods=["GET"])
def trainer_health():
    return jsonify({
        "ok": True,
        "service": "ASXR-TPU Trainer",
        "version": "1.0.0",
        "backend": "QLoRA",
        "ts": now()
    })


@app.route("/trainer/datasets", methods=["GET"])
def trainer_datasets():
    return jsonify({
        "ok": True,
        "datasets": LIST_DATASETS()
    })


@app.route("/trainer/jobs", methods=["POST"])
def trainer_jobs_create():
    """
    Body example:
    {
      "jobType": "train",
      "model": "Qwen/Qwen1.5-0.5B",
      "dataset": "dataset/xjson/train_xjson_seed.jsonl",
      "params": {...},
      "upstream": {...}    # optional from CPU/GPU
    }
    """
    spec = request.get_json(force=True, silent=True) or {}
    job_id = "job-" + uuid.uuid4().hex[:8]

    job = {
      "id": job_id,
      "status": "queued",
      "created_at": now(),
      "updated_at": now(),
      "spec": spec,
      "logs": [],
      "result": None
    }
    JOBS[job_id] = job

    # Launch training in background thread
    t = threading.Thread(target=_run_job_thread, args=(job_id,), daemon=True)
    t.start()

    return jsonify({
      "ok": True,
      "id": job_id,
      "status": job["status"]
    })


def _run_job_thread(job_id: str):
    job = JOBS[job_id]
    try:
        job["status"] = "running"
        job["updated_at"] = now()
        job["logs"].append("Job started")

        result, logs = run_qlora_job(job["spec"], log_fn=lambda msg: job["logs"].append(msg))

        job["status"] = "completed"
        job["updated_at"] = now()
        job["result"] = result
        job["logs"].extend(logs)

    except Exception as e:
        job["status"] = "failed"
        job["updated_at"] = now()
        job["logs"].append(f"ERROR: {repr(e)}")


@app.route("/trainer/jobs/<job_id>", methods=["GET"])
def trainer_jobs_status(job_id):
    job = JOBS.get(job_id)
    if not job:
        return jsonify({"ok": False, "error": "JobNotFound"}), 404
    return jsonify({
        "ok": True,
        "job": job
    })


if __name__ == "__main__":
    # Bind to all interfaces, port 8050 by default
    app.run(host="0.0.0.0", port=8050)
```

