// Enhanced K'uhul VM with DOM integration
class KuhulVM {
  constructor() {
    this.scxMap = new Map([
      ['(?)', 'if'], ['(∴)', 'then'], ['(💬>)', 'message'],
      ['(⊕)', 'increment'], ['⟁', 'atomic_block'], ['⚡', 'async'],
      ['🌀', 'loop'], ['🔒', 'secure'], ['📦', 'package'],
      ['🚀', 'launch'], ['🎯', 'target'], ['💾', 'store'],
      ['📱', 'dom'], ['🎨', 'style'], ['🔧', 'config']
    ]);
    this.stack = [];
    this.vars = new Map();
    this.dom = null;
    this.asxr = null;
  }

  async initialize(context) {
    this.dom = context.dom;
    this.asxr = context.asxr;
    console.log('🎯 K\\'uhul VM initialized with DOM integration');
  }

  decompressSCX(scxCode) {
    return scxCode.split(' ').map(glyph => this.scxMap.get(glyph) || glyph).join(' ');
  }

  async execute(glyphs) {
    const decompressed = this.decompressSCX(glyphs);
    console.log('🎯 K\\'uhul executing:', decompressed);

    const tokens = decompressed.match(/\\[\\w+\\s]*\\]|"[^"]*"|\\w+/g) || [];

    for (const token of tokens) {
      await this.processToken(token);
    }

    return this.stack;
  }

  async processToken(token) {
    if (token.startsWith('[') && token.endsWith(']')) {
      const cmd = token.slice(1, -1).trim();
      await this.executeCommand(cmd);
    } else if (token.startsWith('"') && token.endsWith('"')) {
      this.stack.push(token.slice(1, -1));
    } else {
      this.stack.push(token);
    }
  }

  async executeCommand(cmd) {
    const [action, ...args] = cmd.split(' ');

    switch(action.toLowerCase()) {
      case 'pop':
        this.stack.pop();
        break;
      case 'wo':
        this.stack.push(args.join(' '));
        break;
      case 'sek':
        await this.handleSekCommand(args);
        break;
      case 'xul':
        this.stack = [];
        break;
      case 'store':
        if (args[0] && this.stack.length > 0) {
          this.vars.set(args[0], this.stack[this.stack.length - 1]);
        }
        break;
      case 'load':
        if (args[0] && this.vars.has(args[0])) {
          this.stack.push(this.vars.get(args[0]));
        }
        break;
      case 'dom':
        await this.handleDOMCommand(args);
        break;
      case 'style':
        await this.handleStyleCommand(args);
        break;
      default:
        console.log('Unknown command:', action);
    }
  }

  async handleSekCommand(args) {
    const [subcmd, ...subargs] = args;

    switch(subcmd.toLowerCase()) {
      case 'print':
        const value = this.stack[this.stack.length - 1];
        this.dom.log(value);
        break;
      case 'render':
        await this.dom.render(this.stack);
        break;
      case 'create':
        await this.dom.createElement(subargs[0], this.stack);
        break;
      default:
        console.log('Unknown sek command:', subcmd);
    }
  }

  async handleDOMCommand(args) {
    const [action, ...params] = args;

    switch(action.toLowerCase()) {
      case 'create':
        const [tag, content] = params;
        this.dom.createElement(tag, content || this.stack.pop());
        break;
      case 'append':
        this.dom.appendToRoot(this.stack.pop());
        break;
      case 'clear':
        this.dom.clear();
        break;
      case 'style':
        const [property, value] = params;
        this.dom.applyStyle(property, value);
        break;
    }
  }

  async handleStyleCommand(args) {
    const [element, property, value] = args;
    this.dom.applyStyleToElement(element, property, value);
  }
}

// Minimal DOM manipulation for ASXR
class ASXRDOM {
  constructor() {
    this.root = null;
    this.elements = new Map();
  }

  clear() {
    document.body.innerHTML = '';
    this.elements.clear();
  }

  createRootElement() {
    this.root = document.createElement('div');
    this.root.id = 'asxr-root';
    this.root.style.cssText = \`
      min-height: 100vh;
      background: #020617;
      color: #16f2aa;
      font-family: system-ui;
      padding: 20px;
    \`;
    document.body.appendChild(this.root);
  }

  log(message) {
    const logElement = document.createElement('div');
    logElement.textContent = \`> \${message}\`;
    logElement.style.cssText = \`
      font-family: 'Courier New', monospace;
      margin: 5px 0;
      padding: 5px;
      border-left: 2px solid #16f2aa;
    \`;
    this.root.appendChild(logElement);
  }

  createElement(tag, content) {
    const element = document.createElement(tag);

    if (typeof content === 'string') {
      element.textContent = content;
    } else if (Array.isArray(content)) {
      element.textContent = content.join(' ');
    }

    // Apply ASX default styles
    this.applyASXStyles(element, tag);

    this.elements.set(tag, element);
    return element;
  }

  applyASXStyles(element, type) {
    const styles = {
      panel: \`
        background: #050b1c;
        border: 1px solid rgba(22, 242, 170, 0.3);
        border-radius: 12px;
        padding: 20px;
        margin: 10px;
        box-shadow: 0 0 20px rgba(22, 242, 170, 0.1);
      \`,
      button: \`
        background: linear-gradient(135deg, #16f2aa, #00f5ff);
        border: none;
        padding: 12px 24px;
        border-radius: 25px;
        color: #020617;
        font-weight: bold;
        cursor: pointer;
        margin: 5px;
      \`,
      terminal: \`
        background: #000;
        color: #16f2aa;
        font-family: 'Courier New', monospace;
        padding: 15px;
        border-radius: 8px;
        border: 1px solid #16f2aa;
        min-height: 200px;
      \`,
      default: \`
        margin: 10px 0;
        padding: 10px;
      \`
    };

    element.style.cssText = styles[type] || styles.default;
  }

  appendToRoot(element) {
    if (this.root && element) {
      this.root.appendChild(element);
    }
  }

  applyStyle(property, value) {
    if (this.root) {
      this.root.style[property] = value;
    }
  }

  applyStyleToElement(elementId, property, value) {
    const element = this.elements.get(elementId) || document.getElementById(elementId);
    if (element) {
      element.style[property] = value;
    }
  }

  async render(stack) {
    // Render based on stack contents
    for (const item of stack) {
      if (typeof item === 'string') {
        this.log(item);
      }
    }
  }

  showError(message) {
    const errorElement = document.createElement('div');
    errorElement.textContent = \`❌ ASXR Error: \${message}\`;
    errorElement.style.cssText = \`
      background: #ff6b6b;
      color: white;
      padding: 15px;
      border-radius: 8px;
      margin: 10px 0;
      border: 1px solid #ff4757;
    \`;
    this.root.appendChild(errorElement);
  }
}

// Global ASXR Browser instance
window.ASXRBrowser = ASXRBrowser;`,

