            usleep(100000); // Small delay for effect
            $this->state['boot_steps'][] = $step;
        }

        $this->state['status'] = 'hot';
        $this->log('✅ K\'UHUL BOOTLOADER OPERATIONAL');
        return true;
    }

    public function executeKhl($file) {
        $content = file_get_contents($file);

        // Parse K'UHUL glyph commands
        $lines = explode("\n", $content);
        $output = [];

        foreach ($lines as $line) {
            if (preg_match('/\[(.*?)Pop (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $function = $matches[2];
                $output[] = "🌀 Executing: $glyph $function";
            } elseif (preg_match('/\[(.*?)Wo (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $value = $matches[2];
                $output[] = "  $glyph Writing: $value";
            } elseif (preg_match('/\[(.*?)Sek (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $command = $matches[2];
                $output[] = "  $glyph Executing: $command";
            } elseif (preg_match('/\[(.*?)Xul\]/', $line, $matches)) {
                $glyph = $matches[1];
                $output[] = "  $glyph Returning";
            }
        }

        return $output;
    }

    public function getHealth() {
        return [
            'ok' => empty($this->state['errors']),
            'status' => $this->state['status'],
            'boot_steps' => $this->state['boot_steps'],
            'errors' => $this->state['errors'],
            'uptime_seconds' => time() - $this->state['uptime'],
            'version' => KUHUL_VERSION
        ];
    }

    private function log($message) {
        $this->state['boot_steps'][] = $message;
        return $message;
    }
}

