### **2. K'UHUL Kernel File (kuhul.kernel.php)**

```php
<?php
// ============================================================
// K'UHUL KERNEL - kuhul.kernel.php
// Pure PHP implementation of K'UHUL glyph engine
// ============================================================

class KuhulKernel {
    private $variables = [];
    private $functions = [];
    private $output = [];

    public function executeKhl($khlCode) {
        $this->output = [];

        // Parse glyph commands
        $lines = explode("\n", $khlCode);

        foreach ($lines as $line) {
            $line = trim($line);

            // [Pop] - Define function
            if (preg_match('/\[(.*?)Pop (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $function = $matches[2];
                $this->functions[$function] = ['glyph' => $glyph, 'lines' => []];
                $this->log("🌀 Defining function: $glyph $function");
            }

            // [Wo] - Write literal
            elseif (preg_match('/\[(.*?)Wo (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $value = $this->parseValue($matches[2]);
                $this->log("  $glyph Writing: " . json_encode($value));
            }

            // [Ch'en] - Store variable
            elseif (preg_match('/\[(.*?)Ch\'en (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $varName = $matches[2];
                $this->log("  $glyph Storing to: $varName");
            }

            // [Sek] - Execute operation
            elseif (preg_match('/\[(.*?)Sek (.*?)\]/', $line, $matches)) {
                $glyph = $matches[1];
                $operation = $matches[2];
                $this->log("  $glyph Executing: $operation");
            }

            // [Xul] - Return
            elseif (preg_match('/\[(.*?)Xul\]/', $line, $matches)) {
                $glyph = $matches[1];
                $this->log("  $glyph Returning");
            }

            // [(❓)] - Conditional
            elseif (preg_match('/\[\((❓)\)(.*?)\]/', $line, $matches)) {
                $condition = $matches[2];
                $this->log("  ❓ Conditional: $condition");
            }

            // [then] - Then branch
            elseif (preg_match('/\[then\]/', $line, $matches)) {
                $this->log("  then");
            }

            // [∴] - Else branch
            elseif (preg_match('/\[∴\]/', $line, $matches)) {
                $this->log("  ∴");
            }
        }

        return $this->output;
    }

    private function parseValue($value) {
        // Remove quotes if present
        $value = trim($value);
        if (($value[0] === "'" && $value[-1] === "'") || 
            ($value[0] === '"' && $value[-1] === '"')) {
            return substr($value, 1, -1);
        }

        // Try to parse as JSON
        $json = json_decode($value, true);
        if ($json !== null) {
            return $json;
        }

        // Try to parse as number
        if (is_numeric($value)) {
            return $value + 0;
        }

        // Return as string
        return $value;
    }

    private function log($message) {
        $this->output[] = $message;
        echo "K'UHUL: $message\n";
    }

    public function getOutput() {
        return $this->output;
    }
}

