    <script>
        // K'UHUL SVG RUNTIME ENGINE
        class KuhulSvgRuntime {
            constructor() {
                this.canvas = document.getElementById('kuhul-demo-runtime');
                this.agents = new Map();
                this.connections = new Map();
                this.animationId = null;
            }

            createAgent(id, type, x, y) {
                const agent = document.createElementNS('http://www.w3.org/2000/svg', 'use');
                agent.setAttribute('href', `#kuhul-agent-${type}`);
                agent.setAttribute('x', x);
                agent.setAttribute('y', y);
                agent.setAttribute('data-agent-id', id);
                agent.classList.add('kuhul-agent');

                this.canvas.appendChild(agent);
                this.agents.set(id, agent);
                return agent;
            }

            createConnection(fromId, toId, type = 'connection') {
                const fromAgent = this.agents.get(fromId);
                const toAgent = this.agents.get(toId);

                if (!fromAgent || !toAgent) return;

                const fromX = parseFloat(fromAgent.getAttribute('x'));
                const fromY = parseFloat(fromAgent.getAttribute('y'));
                const toX = parseFloat(toAgent.getAttribute('x'));
                const toY = parseFloat(toAgent.getAttribute('y'));

                const connection = document.createElementNS('http://www.w3.org/2000/svg', 'use');
                connection.setAttribute('href', `#kuhul-${type}`);
                connection.setAttribute('x', fromX);
                connection.setAttribute('y', fromY);
                connection.setAttribute('transform', `rotate(${this.calculateAngle(fromX, fromY, toX, toY)} ${fromX} ${fromY}) scale(${this.calculateDistance(fromX, fromY, toX, toY) / 100})`);

                this.canvas.appendChild(connection);
                this.connections.set(`${fromId}-${toId}`, connection);
            }

            calculateAngle(x1, y1, x2, y2) {
                return Math.atan2(y2 - y1, x2 - x1) * 180 / Math.PI;
            }

            calculateDistance(x1, y1, x2, y2) {
                return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
            }

            animateDataFlow(fromId, toId) {
                const fromAgent = this.agents.get(fromId);
                const toAgent = this.agents.get(toId);

                if (!fromAgent || !toAgent) return;

                const fromX = parseFloat(fromAgent.getAttribute('x'));
                const fromY = parseFloat(fromAgent.getAttribute('y'));
                const toX = parseFloat(toAgent.getAttribute('x'));
                const toY = parseFloat(toAgent.getAttribute('y'));

                const dataParticle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
                dataParticle.setAttribute('r', '3');
                dataParticle.setAttribute('fill', '#9966ff');
                dataParticle.setAttribute('cx', fromX);
                dataParticle.setAttribute('cy', fromY);

                const animateX = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
                animateX.setAttribute('attributeName', 'cx');
                animateX.setAttribute('from', fromX);
                animateX.setAttribute('to', toX);
                animateX.setAttribute('dur', '1s');
                animateX.setAttribute('fill', 'freeze');

                const animateY = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
                animateY.setAttribute('attributeName', 'cy');
                animateY.setAttribute('from', fromY);
                animateY.setAttribute('to', toY);
                animateY.setAttribute('dur', '1s');
                animateY.setAttribute('fill', 'freeze');

                dataParticle.appendChild(animateX);
                dataParticle.appendChild(animateY);
                this.canvas.appendChild(dataParticle);

                setTimeout(() => dataParticle.remove(), 1000);
            }

            clear() {
                this.canvas.innerHTML = '';
                this.agents.clear();
                this.connections.clear();
            }
        }

        // Initialize runtime
        const kuhulRuntime = new KuhulSvgRuntime();

        // Demo Functions
        function startHive() {
            kuhulRuntime.clear();

            // Create agents
            kuhulRuntime.createAgent('queen', 'queen', 400, 150);
            kuhulRuntime.createAgent('coder', 'coder', 300, 100);
            kuhulRuntime.createAgent('analyst', 'analyst', 500, 100);
            kuhulRuntime.createAgent('creative', 'creative', 300, 200);
            kuhulRuntime.createAgent('memory', 'memory', 500, 200);

            // Create connections
            kuhulRuntime.createConnection('queen', 'coder');
            kuhulRuntime.createConnection('queen', 'analyst');
            kuhulRuntime.createConnection('queen', 'creative');
            kuhulRuntime.createConnection('queen', 'memory');
        }

        function simulateTraining() {
            // Add training visualization
            const trainingViz = document.createElementNS('http://www.w3.org/2000/svg', 'use');
            trainingViz.setAttribute('href', '#kuhul-training-loss');
            trainingViz.setAttribute('x', '50');
            trainingViz.setAttribute('y', '50');
            kuhulRuntime.canvas.appendChild(trainingViz);

            // Add progress indicator
            const progress = document.createElementNS('http://www.w3.org/2000/svg', 'use');
            progress.setAttribute('href', '#kuhul-progress-ring');
            progress.setAttribute('x', '700');
            progress.setAttribute('y', '50');
            kuhulRuntime.canvas.appendChild(progress);
        }

        function showDataFlow() {
            const agents = ['coder', 'analyst', 'creative', 'memory'];

            setInterval(() => {
                const from = agents[Math.floor(Math.random() * agents.length)];
                const to = agents[Math.floor(Math.random() * agents.length)];

                if (from !== to) {
                    kuhulRuntime.animateDataFlow(from, to);
                }
            }, 800);
        }

        function resetDemo() {
            kuhulRuntime.clear();
        }

        // Initialize demo
        document.addEventListener('DOMContentLoaded', startHive);
    </script>
</body>
</html>
