/* Text colors */
.ax-text      { color: var(--ax-text); }
.ax-text-muted{ color: var(--ax-muted); }
.ax-text-primary { color: var(--ax-primary); }
.ax-text-secondary { color: var(--ax-secondary); }
.ax-text-accent { color: var(--ax-accent); }

/* Borders */
.ax-border      { border: 1px solid var(--ax-border); }
