  U9 = ULTRA_TRAINER_SCRIPT

  Z0 = BLENDER_BRIDGE
  Z1 = MX2PHP
  Z2 = MX2JS
  Z3 = MX2PY

; CATEGORY SYMBOLS
  @O = core_os
  @E = engines
  @U = ui_templates
  @G = game_systems
  @D = docs
  @S = ultra_scripts
  @X = utilities

; CSS PROFILES
  #P0 = atomic-asx-blocks.css::dark-docs
  #P1 = atomic-asx-blocks.css::quant-ui
  #P2 = atomic-asx-blocks.css::hud-green
#ENDDICT

#BLOCKS
  @O B0 #P0   ; KUHUL_GODMODE_OS -> core_os, docs theme
  @O B1 #P0   ; PRIME_COCKPIT_V1
  @O B2 #P0   ; PRIME_MATRIX_LOGIN
