// XCFE - XJSON Control Flow Engine
class XCFEInterpreter {
  constructor(ctx = {}) {
    this.ctx = ctx;
  }

  resolve(ref) {
    if (typeof ref !== "string" || !ref.startsWith("$")) return ref;
    const path = ref.slice(1).split(".");
    let cur = this.ctx;
    for (const p of path) {
      cur = cur?.[p];
    }
    return cur;
  }

  compare(left, op, right) {
    const l = this.resolve(left);
    const r = this.resolve(right);

    switch (op) {
      case "==": return l == r;
      case "!=": return l != r;
      case ">":  return l >  r;
      case "<":  return l <  r;
      case ">=": return l >= r;
      case "<=": return l <= r;
      case "contains": return String(l).includes(String(r));
      case "in": return Array.isArray(r) ? r.includes(l) : false;
      case "agent_eval": return this.agentEval(l, r);
      default: return false;
    }
  }

  async agentEval(agentId, prompt) {
    const res = await fetch("/api/agent/eval", {
      method: "POST",
      body: JSON.stringify({ agent: agentId, prompt })
    });
    const json = await res.json();
    return json.result;
  }

  async executeBlock(block) {
    // conditionals
    if (block["@if"]) {
      const cond = block["@if"].cond;
      const result = this.compare(cond.left, cond.op, cond.right);
      const branch = result ? block["@if"]["@then"] : block["@if"]["@else"];

      if (branch) {
        for (const subBlock of branch) {
          await this.executeBlock(subBlock);
        }
      }
    }

    // actions
    if (block["@tpu/train"]) {
      await handleTpuTrain(new Request("/api/tpu/train", {
        method: "POST",
        body: JSON.stringify(block["@tpu/train"])
      }));
    }

    if (block["@tpu/checkpoint"]) {
      console.log("Checkpoint operation:", block["@tpu/checkpoint"]);
    }

    if (block["@log"]) {
      console.log("[XCFE LOG]", this.resolve(block["@log"]));
    }
  }
}

// API handler
async function handleXcfeExecute(request) {
  const body = await request.json();
  const { block, context = {} } = body;

  const xcfe = new XCFEInterpreter(context);
  await xcfe.executeBlock(block);

  return new Response(
    JSON.stringify({ ok: true, context: xcfe.ctx }),
    { headers: { "Content-Type": "application/json" } }
  );
}
