// Python AST emulation (toy)
function executePyAst(ast, ctx = {}) {
  const state = { ...ctx };
  for (const node of ast.body) {
    switch (node.type) {
      case "Assign":
        state[node.target] = node.value;
        break;
      case "Expr":
        if (node.value.func === "print") {
          console.log("[PY-EMU]",
            node.value.args.map(arg => state[arg] || arg)
          );
        }
        break;
      case "AugAssign":
        if (node.op === "Add") {
          state[node.target] = (state[node.target] || 0) + node.value;
        }
        break;
    }
  }
  return state;
}

async function handleFileUpload(request) {
  const formData = await request.formData();
  const file = formData.get("training_script");
  const type = formData.get("type") || "py";

  if (!file) {
    return new Response(
      JSON.stringify({ error: "No file provided" }),
      { status: 400 }
    );
  }

  const content = await file.text();
  let ast;

  try {
    if (type === "py") {
      ast = parseSimplePy(content);
    } else if (type === "xjson") {
      ast = JSON.parse(content);
    }

    const result = (type === "py")
      ? executePyAst(ast)
      : await handleXcfeExecute(new Request("/api/xcfe/execute", {
          method: "POST",
          body: JSON.stringify({ block: ast })
        }));

    return new Response(
      JSON.stringify({ ok: true, result }),
      { headers: { "Content-Type": "application/json" } }
    );
  } catch (e) {
    return new Response(
      JSON.stringify({ error: e.message }),
      { status: 500 }
    );
  }
}

// Simple Python pseudo-parser (demo)
function parseSimplePy(code) {
  return {
    type: "program",
    body: [
      { type: "Assign", target: "steps", value: 100 },
      { type: "AugAssign", target: "steps", op: "Add", value: 1 }
    ]
  };
}
