// API endpoints
async function handleTrainingJobs(request) {
  const url = new URL(request.url);
  const jobId = url.searchParams.get("jobId");

  if (jobId) {
    const job = trainingEngine.getJob(jobId);
    return new Response(JSON.stringify({ ok: true, job }), {
      headers: { "Content-Type": "application/json" }
    });
  } else {
    const jobs = trainingEngine.getAllJobs();
    return new Response(JSON.stringify({ ok: true, jobs }), {
      headers: { "Content-Type": "application/json" }
    });
  }
}

async function handleTrainingCancel(request) {
  const body = await request.json();
  const { jobId } = body;
  const success = trainingEngine.cancelJob(jobId);

  return new Response(
    JSON.stringify({ ok: success, jobId }),
    { headers: { "Content-Type": "application/json" } }
  );
}

async function handleCheckpoints(request) {
  const checkpoints = trainingEngine.getCheckpoints();
  return new Response(
    JSON.stringify({ ok: true, checkpoints }),
    { headers: { "Content-Type": "application/json" } }
  );
}

// fetch handler wiring
self.addEventListener("fetch", (event) => {
  const url = new URL(event.request.url);

  if (url.pathname === "/api/training/jobs" && event.request.method === "GET") {
    event.respondWith(handleTrainingJobs(event.request)); return;
  }
  if (url.pathname === "/api/training/cancel" && event.request.method === "POST") {
    event.respondWith(handleTrainingCancel(event.request)); return;
  }
  if (url.pathname === "/api/training/checkpoints" && event.request.method === "GET") {
    event.respondWith(handleCheckpoints(event.request)); return;
  }

  // … existing routes
});

self.addEventListener("message", (event) => {
  if (event.data && event.data.type === "GET_TRAINING_UPDATE") {
    const jobs = trainingEngine.getAllJobs();
    event.ports[0].postMessage({ type: "TRAINING_JOBS", jobs });
  }
});
