#!/usr/bin/env python
"""
ASX ULTRA Training Bridge
Connects browser TPU-OS to real QLoRA training
"""
import asyncio, websockets, json, logging, re
from dataclasses import dataclass
from typing import Dict, Any
import asyncio.subprocess as asp

@dataclass
class TrainingJob:
    job_id: str
    checkpoint_id: str
    steps: int
    config: Dict[str, Any]
    status: str = "queued"
    progress: float = 0.0

class TrainingBridge:
    def __init__(self):
        self.active_jobs: Dict[str, TrainingJob] = {}
        self.connected_clients = set()

    def parse_training_output(self, line: str) -> float:
        """
        Expect lines like: "step 100/1000 loss: 1.23"
        """
        m = re.search(r"step\\s+(\\d+)/(\\d+)", line)
        if not m:
          return 0.0
        cur, total = int(m.group(1)), int(m.group(2))
        return (cur / total) * 100.0

    async def broadcast_update(self, job: TrainingJob):
        payload = {
            "type": "training_update",
            "job_id": job.job_id,
            "status": job.status,
            "progress": job.progress
        }
        data = json.dumps(payload)
        for ws in list(self.connected_clients):
            try:
                await ws.send(data)
            except Exception:
                self.connected_clients.discard(ws)

    async def handle_training_request(self, job: TrainingJob):
        job.status = "training"
        cmd = [
          "python", "asx_ultra_trainer_qlora.py",
          "--model", "Qwen/Qwen1.5-0.5B",
          "--data", "dataset/xjson/train_xjson_seed.jsonl",
          "--out", f"checkpoints/{job.job_id}",
          "--max-steps", str(job.steps),
          "--device", job.config.get("device", "cuda")
        ]
        try:
            proc = await asp.create_subprocess_exec(
                *cmd,
                stdout=asp.PIPE,
                stderr=asp.STDOUT
            )
            async for raw in proc.stdout:
                line = raw.decode(errors="ignore").strip()
                p = self.parse_training_output(line)
                if p:
                    job.progress = p
                    await self.broadcast_update(job)
            await proc.wait()
            job.status = "completed" if proc.returncode == 0 else "failed"
        except Exception as e:
            logging.exception("Training failed")
            job.status = "error"
        await self.broadcast_update(job)

bridge = TrainingBridge()

async def ws_handler(ws, path):
    bridge.connected_clients.add(ws)
    try:
        async for msg in ws:
            data = json.loads(msg)
            if data.get("type") == "start_training":
                job = TrainingJob(
                  job_id=data["job_id"],
                  checkpoint_id=data["checkpoint_id"],
                  steps=data.get("steps", 100),
                  config=data.get("config", {})
                )
                bridge.active_jobs[job.job_id] = job
                asyncio.create_task(bridge.handle_training_request(job))
    finally:
        bridge.connected_clients.discard(ws)

if __name__ == "__main__":
    server = websockets.serve(ws_handler, "0.0.0.0", 8765)
    loop = asyncio.get_event_loop()
    loop.run_until_complete(server)
    loop.run_forever()
