// Training Dashboard JavaScript
class TrainingDashboard {
  constructor() {
    this.activeJobs = new Map();
    this.currentJobId = null;
    this.updateInterval = null;

    this.initializeElements();
    this.setupEventListeners();
    this.startPolling();
  }

  initializeElements() {
    this.startTrainingBtn = document.getElementById('startTrainingBtn');
    this.stopTrainingBtn = document.getElementById('stopTrainingBtn');
    this.trainingCheckpoint = document.getElementById('trainingCheckpoint');
    this.trainingSteps = document.getElementById('trainingSteps');
    this.trainingJobsList = document.getElementById('trainingJobsList');
    this.trainingProgress = document.getElementById('trainingProgress');
    this.progressBar = document.getElementById('progressBar');
    this.progressPercent = document.getElementById('progressPercent');
    this.progressJobId = document.getElementById('progressJobId');
    this.progressLoss = document.getElementById('progressLoss');
    this.progressTime = document.getElementById('progressTime');
    this.trainingLog = document.getElementById('trainingLog');
  }

  setupEventListeners() {
    this.startTrainingBtn.addEventListener('click', () => this.startTraining());
    this.stopTrainingBtn.addEventListener('click', () => this.stopTraining());
  }

  async startTraining() {
    const checkpoint = this.trainingCheckpoint.value;
    const steps = parseInt(this.trainingSteps.value) || 100;

    try {
      const response = await fetch('/api/tpu/train', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          checkpointId: checkpoint,
          steps: steps,
          config: {
            lr: 2e-4,
            batch_size: 1,
            grad_accum: 8
          }
        })
      });

      const result = await response.json();

      if (result.ok) {
        this.addTrainingLog(`Started training job: ${result.jobId}`);
        this.currentJobId = result.jobId;
        this.stopTrainingBtn.disabled = false;
        this.startTrainingBtn.disabled = true;
      } else {
        this.addTrainingLog(`Error: ${result.error}`);
      }
    } catch (error) {
      this.addTrainingLog(`Network error: ${error.message}`);
    }
  }

  async stopTraining() {
    if (!this.currentJobId) return;

    try {
      const response = await fetch('/api/training/cancel', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ jobId: this.currentJobId })
      });

      const result = await response.json();

      if (result.ok) {
        this.addTrainingLog(`Cancelled training job: ${this.currentJobId}`);
        this.stopTrainingBtn.disabled = true;
        this.startTrainingBtn.disabled = false;
      }
    } catch (error) {
      this.addTrainingLog(`Error cancelling job: ${error.message}`);
    }
  }

  async updateTrainingStatus() {
    try {
      const response = await fetch('/api/training/jobs');
      const result = await response.json();

      if (result.ok) {
        this.updateJobsList(result.jobs);
        this.updateProgress(result.jobs);
      }
    } catch (error) {
      console.error('Failed to fetch training status:', error);
    }
  }

  updateJobsList(jobs) {
    this.trainingJobsList.innerHTML = '';

    jobs.forEach(job => {
      const jobEl = document.createElement('div');
      jobEl.style.cssText = 'padding: 6px; margin-bottom: 4px; background: rgba(22,242,170,0.1); border-radius: 4px; font-size: 10px;';

      const statusColor = {
        'queued': '#ffa500',
        'training': '#16f2aa', 
        'completed': '#00ffff',
        'cancelled': '#ff6b6b'
      }[job.status] || '#7a8699';

      jobEl.innerHTML = `
        <div style="display: flex; justify-content: between; align-items: center;">
          <span style="font-weight: bold;">${job.id.slice(0, 8)}</span>
          <span style="color: ${statusColor}; font-size: 9px;">${job.status.toUpperCase()}</span>
        </div>
        <div style="font-size: 9px; color: var(--text-secondary);">
          ${job.config.checkpointId} • ${job.progress.toFixed(1)}%
        </div>
      `;

      this.trainingJobsList.appendChild(jobEl);
    });
  }

  updateProgress(jobs) {
    const activeJob = jobs.find(job => job.status === 'training');

    if (activeJob) {
      this.trainingProgress.style.display = 'block';
      this.progressBar.style.width = `${activeJob.progress}%`;
      this.progressPercent.textContent = `${activeJob.progress.toFixed(1)}%`;
      this.progressJobId.textContent = activeJob.id.slice(0, 8);

      // Update loss
      const latestLog = activeJob.logs[activeJob.logs.length - 1];
      if (latestLog && latestLog.loss) {
        this.progressLoss.textContent = `Loss: ${latestLog.loss}`;
      }

      // Update ETA
      if (activeJob.startTime && activeJob.progress > 0) {
        const elapsed = Date.now() - activeJob.startTime;
        const totalEst = elapsed / (activeJob.progress / 100);
        const remaining = Math.max(0, totalEst - elapsed);
        this.progressTime.textContent = `ETA: ${this.formatTime(remaining)}`;
      }
    } else {
      this.trainingProgress.style.display = 'none';
    }
  }

  formatTime(ms) {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) return `${hours}h ${minutes % 60}m`;
    if (minutes > 0) return `${minutes}m ${seconds % 60}s`;
    return `${seconds}s`;
  }

  addTrainingLog(message) {
    const timestamp = new Date().toISOString().split('T')[1].replace('Z', '');
    this.trainingLog.textContent += `[${timestamp}] ${message}\n`;
    this.trainingLog.scrollTop = this.trainingLog.scrollHeight;
  }

  startPolling() {
    this.updateInterval = setInterval(() => {
      this.updateTrainingStatus();
    }, 2000);

    // Initial update
    this.updateTrainingStatus();
  }

  destroy() {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }
  }
}

