// WebSocket bridge for real training
class TrainingWebSocketBridge {
  constructor() {
    this.ws = null;
    this.reconnectAttempts = 0;
    this.maxReconnectAttempts = 5;
  }

  connect() {
    try {
      this.ws = new WebSocket('ws://localhost:8765');

      this.ws.onopen = () => {
        console.log('[ULTRA-TRAIN] Connected to training server');
        this.reconnectAttempts = 0;
      };

      this.ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        this.handleTrainingUpdate(data);
      };

      this.ws.onclose = () => {
        console.log('[ULTRA-TRAIN] Training server disconnected');
        this.attemptReconnect();
      };

      this.ws.onerror = (error) => {
        console.error('[ULTRA-TRAIN] WebSocket error:', error);
      };

    } catch (error) {
      console.error('[ULTRA-TRAIN] Failed to connect to training server:', error);
    }
  }

  attemptReconnect() {
    if (this.reconnectAttempts < this.maxReconnectAttempts) {
      this.reconnectAttempts++;
      setTimeout(() => this.connect(), 2000 * this.reconnectAttempts);
    }
  }

  handleTrainingUpdate(data) {
    // Broadcast to all clients
    if (self.clients) {
      self.clients.matchAll().then(clients => {
        clients.forEach(client => {
          client.postMessage({
            type: 'REAL_TRAINING_UPDATE',
            data
          });
        });
      });
    }
  }

  sendTrainingRequest(jobData) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(jobData));
      return true;
    }
    return false;
  }
}

// Initialize WebSocket bridge
