  const body = await request.json();
  const jobId = 'real_job_' + Date.now();

  // Try WebSocket first
  const wsSuccess = trainingBridge.sendTrainingRequest({
    type: 'start_training',
    job_id: jobId,
    ...body
  });

  if (wsSuccess) {
    return new Response(
      JSON.stringify({ 
        ok: true, 
        jobId,
        mode: 'real_training',
        message: 'Real QLoRA training started'
      }),
      { headers: { 'Content-Type': 'application/json' } }
    );
  } else {
    // Fallback to simulated training
    return handleTpuTrain(request);
  }
}
